/* Author: Ricardo Mart√≠nez */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com") {
    /* Remove ads+ */
    div.ego_column {display: none !important;}
    
    /* Remove big "What's on your mind" text box from News Feed */
    div#pagelet_composer {display: none !important;}

    /* Remove trending articles, trending videos, etc. */
    li[class^="uiUnifiedStory uiStreamStory genericStreamStory uiStreamStoryAttachmentOnly"] div[class="storyInnerContent storyContent"] {display: none !important;}
}
