style.css:
/* Generic */
@-moz-document regexp("http(s)?://[a-z0-9]+\\.google\\.[a-z]+(\\.[a-z]+)?/.*") {
	html, body, td, input, textarea, select, button, .g, .kno-ecr-pt {
		font-family: sans-serif !important;
	}

	#onegoogbar, #onegoogbar.new-onegpad {
		font-family: sans-serif !important;
		font-size: small !important;
	}

	div[class*='gb_'] {
		font-family: sans-serif !important;
	}


	.gbqfif, .gbqfsf, .ab_button {
		font-family: sans-serif !important;
	}

	.loadingstatusbox {
		font-family: sans-serif !important;
	}
	.gb_b.gb_d a {
		font: 13px/27px sans-serif !important;
	}
	/* Contact picture selector */
	.a-wb-Z .d-rb-sc-ka {
		font-family: sans-serif !important;
	}
	._E8k {
		font-family: sans-serif-medium,sans-serif !important;;
	}
	.vk_ans, .vk_h, .vk_sh, .vk_txt, ._yz {
		font-family: sans-serif !important;
	}
}

/* GMail */
@-moz-document domain("mail.google.com") {
	.R5, .d8, .Mh, .abX .abY, .py, .R5.Rh, .Dw, .EW, .Dw .J-M, .hU, .hV, .ha, .py, .Kj-JD-Jz, .Kj-JD-Jl, .Kj-JD-Jl button, .Kj-JD-K7, .ah {
		font-family: sans-serif !important;
	}
	.LW-avf {
		font-family: sans-serif !important;
	}
	.ar {
		font-family: sans-serif !important;
		font-size: 11px; 
	}
	/* Inline display of search results */
	.asor_b, .asor_u {
		font: 14px sans-serif !important;
	}
	.asor_c, .asor_d, .asor_e, .asor_h, .asor_i {
		font:12px sans-serif !important;
	}
}

/* Search */
@-moz-document regexp("http(s)?://www\\.google\\.[a-z]+(\\.[a-z]+)?/search.*") {
	li.head, li.g, body, html, .std, h1, input, .g, .c h2, #cdr_min, #cdr_max, .cpbb, .kpbb, .kprb, .kpgb, .kpgrb, .ksb {
		font-family: sans-serif !important;
	}
}

/* Calendar */
@-moz-document regexp("http(s)?://(www|calendar)\\.google\\.[a-z]+(\\.[a-z]+)?/calendar/.*") {
	DIV.calListLabel, DIV.calListLabel-sel, DIV.st-c-pos, .dp-cell, DIV.pac-container {
		font-family: sans-serif !important;
	}

	.eb-root, .cb-r.k-q-Qeoot, th.cb-key, .ac-renderer, .ep-gl-guest
	, div.tile.period-tile, div.tile.select-tile, div.tile.split-tile.action-tile, div.tile.split-tile.duedate-tile {
		font-family: sans-serif !important;
	}
	/* Tasks seem to appear under calendar */
	.U, .w, .j {
		font-family: sans-serif !important;
	}
	.neb-root {
		font: 13px sans-serif !important;
	}
}

/* Drive */
@-moz-document domain("drive.google.com"), domain("docs.google.com") {
	.goog-tree-item-label, .doclist-td, .doclist-td-blank, .doclist-td-checkbox, .doclist-td-content, .doclist-td-date, .doclist-td-modifiers, .doclist-td-name, .doclist-td-owners, .doclist-td-quota, .doclist-td-share-date, .doclist-td-spacing, .doclist-td-star, .docs-menubar, .goog-listitem-caption, .goog-listitem-subcaption, .goog-listheaderitem-caption, .goog-listheaderitem-subcaption, .docs-menubar, .w-t-m, #t-formula-bar-input, .k-q-Qe {
		font-family: sans-serif !important;
	}
}

/* Plus */
@-moz-document domain("plus.google.com") {
	.cw, .yd, .VcZ, .f4a, .Pac {
		font-family: sans-serif !important;
	}
	.xQb, Nac, .Vzc {
		font-family: sans-serif !important;
	}

}

/* Talk gadget */
@-moz-document domain("talkgadget.google.com") {
	TD, DIV {
		font-family: sans-serif !important;
	}
}

/* Hangouts gadget */
@-moz-document domain("hangouts.google.com") {
	TD, DIV {
		font-family: sans-serif !important;
	}
}

/* Youtube */
@-moz-document domain("youtube.com") {
	body, input, button, textarea {
		font-family: sans-serif !important;
	}
}
