style.css:
/*
 * 百度贴吧图片缩放增强脚本 Baidu Tieba Image Scaling Enhancer 宽屏样式
 * version 0.73
 */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("^https?://tieba\\.baidu\\.com/((f\\?kz=.*)|(p/.*))"),
               url-prefix("http://tieba.baidu.com/f?kz="),
               url-prefix("http://tieba.baidu.com/p/"),
               regexp("http://tieba\\.baidu\\.com/f\\?(.*)ct=(.*)z=(.*)"),
               url-prefix("http://tieba.baidu.com.cn/f?kz="),
               url-prefix("http://tieba.baidu.com.cn/p/"),
               regexp("http://tieba\\.baidu\\.com\\.cn/f\\?(.*)ct=(.*)z=(.*)"),
               url-prefix("http://post.baidu.com/f?kz="),
               url-prefix("http://post.baidu.com/p/"),
               regexp("http://post\\.baidu\\.com/f\\?(.*)ct=(.*)z=(.*)"),
               url-prefix("http://post.baidu.com.cn/f?kz="),
               url-prefix("http://post.baidu.com.cn/p/"),
               regexp("http://post\\.baidu\\.com\\.cn/f\\?(.*)ct=(.*)z=(.*)") {
    #container, div.content, #tb_nav, div.p_thread, #pb_content, div.left_section, #j_core_title_wrap, div.l_post, div.d_post_content_main, div.core_reply_wrapper, div.pb_footer, div.d_sign_split, blockquote.d_quote, blockquote.d_quote fieldset, .d_quote .quote_content, div.core { width: auto !important; }
    div.pb_content { background: none !important; }
    div.d_post_content_main { padding: 15px 10px 5px !important; }
    div.p_content { padding: 0 !important; }
    div.left_section {
        -moz-box-sizing: border-box;
        -webkit-box-sizing: border-box;
        box-sizing: border-box;
        width: -moz-calc(100% - 240px) !important;
        width: -webkit-calc(100% - 240px) !important;
        width: calc(100% - 240px) !important;
        border-right: 1px solid #E5E5E5;
    }
    div.l_post {
        background-color: white;
        background-image:    -moz-linear-gradient(left, #F7F7F7, #F7F7F7 130px, white 130px, white);
        background-image: -webkit-linear-gradient(left, #F7F7F7, #F7F7F7 130px, white 130px, white);
        background-image:     -ms-linear-gradient(left, #F7F7F7, #F7F7F7 130px, white 130px, white);
        background-image:      -o-linear-gradient(left, #F7F7F7, #F7F7F7 130px, white 130px, white);
        background-image:         linear-gradient(to right, #F7F7F7, #F7F7F7 130px, white 130px, white);
    }
    div.core_reply_tail, div.core_reply_wrapper { margin-right: 0px !important; }
    div.core_reply, div.d_content { margin-left: 0px !important; max-width: 568px; margin-right: auto !important; }
    #tb_rich_poster { margin-left: 0px !important; }
    #tb_rich_poster_container { margin-left: 10px !important; }
    div.d_main_section, div.d_post_content_main, div.d_content, div.core_reply, h1.core_title_txt { float: none !important; }
    div.d_post_content_main { margin-left: 130px !important; }
    div.right_section { margin-left: 0 !important; }
    h1.core_title_txt { display: inline; }
    .card_top_theme2 { margin-right: 0 !important; }
    .exp { width: 133px !important; }
    .exp_bar { margin-right: 0 !important; }
    div.post_bubble_top, div.post_bubble_bottom { display: none !important; }
    div.post_bubble_middle { width: auto !important; background: none !important; padding: 0 !important; }
}
