style.css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("pixiv.net") {

/* General Advertisement removal */
.ad,
.ad-footer,
.hover-item[style*="border: 2px solid tomato;"],
.ad-printservice,
.ad-printservice ~ *,
[id*="dver"],[class*="dver"],
[id*="ads"], [id*="Ads"],
[class*="ads"], [class*="Ads"],
iframe[src*="ads"] {display: none !important;}

/* ===== Remove annoyances */

/* Remove Premium Objects */
[class*="remium"],
[data-tooltip*="remium"],
[href*="premium.php"],

/* Disabled Javascript warning */
noscript,

/* "NEW" caption */
._new-feature,
.badge-new {
display: none !important;
}

/* ===== General */

/* Default Background Color, No Image */
body {background: none #E4E7EE !important;}

/* History and Back to Top container */
._toolmenu {display: none !important;}

/* History */
._balloon-history,
.item.history-orange {display: none !important;}

/* Back to top button */
#back-to-top {display: none !important;}

/* Pixiv Encyclopedia + Pixiv Comic Icon, Booth.pm */
.pixpedia,
.icon-pixpedia,
.sprites-pixpedia, .sprites-pixpedia + .icon-text,
.icon-pixpedia-no-item,
.icon-pixcomic,
.tabs [href*="booth.pm"],
.column-label [href*="booth.pm"],
._icon.sprites-icon-booth-tag {display: none !important;}

/* breadcrumb */
.breadcrumb {display: none !important;}

/* Footer */
.footer {display: none !important;}

/* ===== Site Header */

/* Pixiv Logo */
#global-header .logo,
.header h1.title {display: none !important;}

/* Home | ホーム */
/*.home {display: none !important;} */

/* Submit | Manage / 作品投稿 | 管理 */
.upload {display: none !important;}

/* Bookmarks | ブックマーク */
.bookmarks {display: none !important;}

/* Staccfeed | スタックフィード */
.stacc {display: none !important;}

/* Message Area ( Envelope and Rabbit ) */
.notification,
.notifications {display: none !important;}

/* Novel + Event box / 小説 + イベント */
.category-menu,
.category-list {display: none !important;}

/* Help / ヘルプ */
.header-help {display: none !important;}

/* ===== @ Homepage ( pixiv.net/mypage.php )*/

/* Info / お知らせ */
#page-mypage .NewsTop {display: none !important;}

/* Pixiv Premium Promotion / pixivプレミアム1ヶ月分無料キャンペーン実施中 */
._unit[style*="border:2px solid tomato;"] {display: none !important;}

/* New illustrations / みんなの新着作品 */ 
.item.everyone-new-illusts {display: none !important;}

/* Spotlight / pixivスポットライト */
.item.spotlight {display: none !important;}

/* featured tags / 注目のタグ */
.item.featured-tags > header,
.item.featured-tags .more,
.item.featured-tags .standard-tag {display: none !important;}

/* Contests + tag cloud / 企画目録 */
.item.contests {display: none !important;}

/* Booth */
#booth-event-items,
#booth-follow-items {display: none !important}

/* Pixiv Touch + Advertisement */
#touch_introduction,
#touch_introduction ~ * {display: none !important;}

/* Recruit */
.recruit-ad {display: none !important;}

/* Pixiv Comic / pixivコミック */
.side-menu.comic-container,
.item.comic-container {display: none !important;}

/* Featured Pixivpedia Articles / ピクシブ百科事典の人気記事 */
.side-menu.dic-ranking,
#dic_ranking {display: none !important;}

/* Groups and Featured Groups / グループ + HOTグループ*/
.side-menu.group-list {display: none !important;}

/* ===== @ Illust Search + Bookmark ( pixiv.net/search.php? , pixiv.net/bookmark.php ) */

/* pixivコミックの注目漫画 */
.comic-hot-works {display: none !important;}

/* Member showcase @ bottom of page when searching tags */
.sample-user-search {display:none !important;}

/* Showcase / ショーケース */
.image-item.showcase {display: none !important;}

/* Hide popular image sort notice */
#popular-trial {display: none !important;}

/* Bookmark tabs ranges / filter */
._bookmark-ranges {display: none !important;}

/* Search - Size / サイズ */
.context .selector:nth-of-type(1),
.column-menu .select-item:nth-of-type(1) {display: none !important;}

/* Search - Orientation / 縦横比 */
.context .selector:nth-of-type(2),
.column-menu .select-item:nth-of-type(2) {display: none !important;}

/* Search - Tools Used / ツール */
.context .selector:nth-of-type(3),
.column-menu .select-item:nth-of-type(3) {display: none !important;}

/* Popular Introduction / の人気作品 */
.popular-introduction {display: none !important;}

/* Related Tags / 関連タグ */
.pager ~ .related,
footer > .related,
.column-related.inline-list {visibility: hidden !important;}

/* Showcase / ショーケース */
.user-ad-container {display: none !important;}

/* Image title - START */
.image h1,
.image-item h1,
.image h2 {display: none !important;}

.display_works > ul > li[id] > br {display: none !important;}
.display_works > ul > li[id] > a,
.linkStyleWorks > ul > li > a  {font-size: 0 !important;}
/* Image title - END */

/* Author */
.image .user,
.image-item .user,
.display_works .bookmark_artist,
.display_works .bookmark_artist + span {display: none !important;}

/* Illust. Bookmark Count */
.bookmark-count {display: none !important;}

/* Illust. Response Count */
.count-list .image-response-count {display: none !important;}

/* ===== @ Member Profile ( pixiv.net/member.php?id= ) */

/* avatar */
.avatar_m > img,
.user-image {
visibility: hidden !important; 
height: 1em !important; 
overflow: hidden !important;
}

/* Add to Mypixiv */
.person_menu #mypixiv-container,
#mypixiv-button {display: none !important;}

/* Send Message */
.person_menu .icon_mail,
.user-relation a[href*="msg_send.php"] {display: none !important;}

/* Premium Badge */
.premium_rect {display: none !important;}

/* Stacc */
#staccOnRight {display: none !important;}

/* Profile table / プロフィール */
.worksListOthersTitle + .ws_table {display: none !important;}

/* ===== @ Illust Page ( pixiv.net/member_illust.php?mode=medium&illust_id= ) */

/* Melonbooks */
#melon_items {display: none !important;}

/* Thumbnail title preview */
.sibling-items img {display: none !important;}

/* Large Title */
.work-info .title {display: none !important;}

/* Bookmark | ブックマークに追加 */
.bookmark {display: none !important;}

/* Illust. tool creation */
.meta .tools {display: none !important;}

/* Poll / Questionaire */
.work-info .has-questionnaire,
.work-info .questionnaire {display: none !important;}

/* Illust. statistics */
.score > dl {display: none !important;}

/* Rating Stars */
.score > .rating {display: none !important;}

/* Description|Caption */
.work-info .caption {display: none !important;}

/* Share objects (Twitter, Facebook, etc.) */
.share-button {display: none !important;}

/* Illust. Tags / タグ */
.work-tags {display: none !important;}

/* Portal tag link "c" */
.tag .portal {display: none !important;}

/* Edit Tags / タグ編集 */
.work-tags .edit {display: none !important;}

/* comments and share box / コメント + シェア */
#one_comment,
.work-tags ~ .works_info {
visibility: hidden !important; 
height: 0 !important; 
overflow: hidden !important;
}

/* Footer Shortcut */
._shortcut-help {display: none !important;}

}
