style.css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document

url-prefix("http://www.mediafire.com/download"),
url-prefix(http://www.mediafire.com/file/) {

/* BG color change */
body {
background: #f2f2f2 !important;
}

/* Remove formatting */
#header {
position: static !important;
}

#container,
#content_container,
.dl_options_innerblock {
padding: 0 !important;
margin: 0 !important;
}

#download_visible_wrapper,
.dl_options_innerblock,
.dl-page .center,
.promo-dl-box,
.dlInfo{

position: static !important;
float: none !important;
height: auto !important;
width: auto !important;
}

.dl-btn-container {
position: relative !important;
top: 0 !important;
}

/* increase label width */
.dl-btn-label {
width: 500px !important;
}

/* background color */
.promo-dl-box {
background: none !important;
}

/* Captcha fixes */

#captchaprotected,
#form_captcha .dl_startlink {

margin-left: auto !important;
margin-right: auto !important;
}

#form_captcha .dl-header-group {
position: relative !important;
}

#form_captcha .dl_startlink,
#form_captcha .dl-box{
position: static !important;
}

#form_captcha {
height: auto !important;
}

/* Remove */
.pool-promo-ad,
#header,
#recaptcha_privacy,
.captchaInstructions,
.captchaPromo,
.captchaSteps,
#download-dark-screen,
object,
iframe,
#logged_in_info,
.top,
.left,
.dlPromoContent,
.bottomAdLeaderboard,
.socialContainerWrap,
.dl-btn-container .dl-utility-nav,
.dlInfo,
#footer

{display: none !important;}

}
