style.css:
@-moz-document domain("www.cracked.com"){

/* Remove a bunch of unneeded crap */

.hidden-sm
{
    display: none !important;
}
/* Remove ads */

#headerBannerAd,
.bannerAd,
.inject-ad,
.mainAd,
.ads-wrapper,
.ads-box,
.ad300x250_300x600,
img[src*="adsystem.com"],
.ad728x90,
.injected-ad,
.d-lg-block
{
    display: none !important;
}
/* Remove popups */

.preloaded_lightbox,
.fb_digioh-overlay,
.sumome-vex
{
    display: none !important;
}

body.sumome-vex-open
{
    overflow: auto !important;
}
/* Disable Background Ad Click Events */

body.has-skin
{
    pointer-events: none;
}

body.has-skin div
{
    pointer-events: auto;
}

html, body {
    overflow-x: visible;
}

/* Remove social media junk */

.social-share-bottom,
.persistent-share,
#footer-stay-connected,
.facebook,
.twitter,
.google-plus,
.stumbleupon,
.reddit,
.pinterest,
.whatsapp,
.btn-more-share,
.btn-facebook,
.btn-twitter
{
    display: none !important;
}
/* Remove junk */

#footer,
.go-down,
.content-top-cards,
.sidebar,
#relatedArticle,
.continue-reading,
#recommendedForYourPleasure,
#moreRecommendedArticles,
.popular-module,
.trc_related_container,
.contentInnerWrapper.entryWrapper .popular-videos-module,
.OUTBRAIN,
#morePodCastModule,
.content-card-top
{
    display: none !important;
}
/* Wide articles */

.col-md-8,
.col-lg-9,
.col-12,
.content-left,
#content-left
{
    width: 100% !important;
    flex: 0 0 100%;
    max-width: 100%;
}
/* Bigger article images and youtube videos */

section p img,
article p img,
p[align=center] img
{
    display: block !important;
    margin-left: auto !important;
    margin-right: auto !important;
    height: auto !important;
    width: auto !important;
    min-width: 70% !important;
}

font[style^="color: #909090;display:block;margin-top: -10px;height: 5px"],
font[style^="color: #909090; display:block; margin-top: -10px; height: 5px;"]
{
    width: auto !important;
    max-width: 70% !important;
}

@media only screen and (min-width: 1281px)
{
    p iframe[src*="youtube.com/embed"], p iframe[src*="youtube.com/embed"]
{
        width: 854px !important;
        height: 480px !important;
    }
}

@media only screen and (min-width: 1601px)
{
    p iframe[src*="youtube.com/embed"], p iframe[src*="youtube.com/embed"]
{
        width: 1280px !important;
        height: 720px !important;
    }
}
/* Tweaks */

#homeWrapper
{
    margin-top: 20px !important;
}

.comment-submission-form .col-md-4
{
    width: 100% !important;
}


.social-share-top .col
{
    display: none;
}
.social-share-top .col.btn-social-favorites
{
    display: inherit;
}

.social-share li
{display: none}
.social-share li.social
{display: inherit}
.social-share
{
    float: right !important;
}

@media (max-width: 767px)
{
    .contentInnerWrapper h1 {
    margin-bottom: 0px !important;

}
    .social-share
{
        height: 0px !important;
        overflow: visible !important;
    position: relative !important;
    top: 15px !important;
    }
}
@media (max-width: 480px)
{
    .social-share li.social
{
        width: 44px !important;
    }
}

.modal.modal-login .modal-dialog .modal-content .modal-header .nav-tabs li.active a
{
    border-bottom: 0px !important;
}
}
