style.css:
/* codemirror/theme/neat.css */

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document
  url-prefix("chrome://stylish/content/edit.xul"),
/* so Userstyles sorts it as an editor style */
  url-prefix("about:") { }
/* so Stylish popup lists this style on any about: page */

@-moz-document regexp("data:text/html.*rel='stylesheet'%20href='chrome://browser/content/devtools/codemirror/codemirror.css'.*")
{

.cm-s-mozilla span.cm-comment { color: #a86; }
.cm-s-mozilla span.cm-keyword { line-height: 1em; font-weight: bold; color: blue; }
.cm-s-mozilla span.cm-string,
.cm-s-mozilla span.cm-string-2 { color: #a22; }
.cm-s-mozilla span.cm-builtin { line-height: 1em; font-weight: bold; color: #077; }
.cm-s-mozilla span.cm-special,
.cm-s-mozilla span[class="cm-error"] { line-height: 1em; font-weight: bold; color: #0aa; }
.cm-s-mozilla span.cm-variable,
.cm-s-mozilla span.cm-variable-1,
.cm-s-mozilla span.cm-variable-2 { color: black; }
.cm-s-mozilla span.cm-number,
.cm-s-mozilla span.cm-atom { color: #3a3; }
.cm-s-mozilla span.cm-meta {color: #555;}
.cm-s-mozilla span.cm-link { color: #3a3; }
.cm-s-mozilla span.cm-tag,
.cm-s-mozilla span.cm-property.cm-error  { color: inherit; }

}
