style.css:
@-moz-document domain("torrentz2.eu") {
/* Torrentz dark & clean - 1.1 */
body {
background:#000!important;
color:#ccc!important
}
h2,div.downlinks {
color:#fff!important;
border-bottom:0!important;
background:#333!important
}
dl a,div.files li {
color:#ccc!important
}
div.top {
background:#222!important;
border-radius:0!important;
margin-left:0!important;
border:1px solid #666!important
}
div.top ul *,form.profile>fieldset>legend,form.profile>fieldset>input.i,div.results>p>span>span,div.results>p>span>a,div.downlinks>dl>dt>a:hover {
background:#222!important
}
div.top ul li {
border-left:1px solid #666!important
}
div.top ul li a:hover,ul.autocomplete>li.selected {
border-bottom:0!important;
background:#555!important;
border-radius:0!important
}
fieldset {
padding:10px 0!important
}
form.search,div.downlinks>dl>dd {
border-radius:0!important;
background:#000!important
}
#thesearchbutton {
background:#222!important;
border-radius:0!important;
border:1px solid #555!important;
color:#eee!important
}
#thesearchbutton:hover {
background:#333!important
}
div.results {
margin:0!important;
border-radius:0!important;
background:#333!important
}
#thesearchbox {
background:#222!important;
color:#ddd!important;
border:1px solid #555!important;
border-radius:0!important
}
span.v {
background:#222!important
}
div.results h3 {
background:#222!important;
border:0!important
}
dl {
background:#000!important;
border-bottom:1px dashed #333!important
}
.results>dl:hover,div.trackers,form.profile>fieldset,div.results>dl:hover>dt,div.download>dl>dt>a:hover,div.votebox > a.up:hover,div.votebox > a.upm:hover,.head,div.results>p>span>a:hover {
background:#333!important
}
dl dd {
background:none
}
dl dt a:hover {
background:none
}
div.download {
margin-top:0!important;
background:#222!important;
border-radius:0!important
}
div.download div {
color:#aaa!important
}
p,div.SemiAcceptableAds>h3 {
background:#222!important;
border-top:0!important;
color:#aaa!important
}
div.files ul,dt,div.download>dl>dd {
background:#000!important
}
ul.autocomplete,.comment {
background:#222!important;
border-color:#888!important
}
ul.autocomplete>li {
border-color:#888!important
}
div.note {
background:#111!important
}
form.profile>fieldset>input.i {
color:#aaa
}
.head,.com {
border:none!important
}
#votebox>a.up:hover, #votebox>a.upm {
background-color:#000!important;
}
.comment {
border-color:#444!important
}
iframe,.SemiAcceptableAds,.downlinks>dl:nth-child(3),.footer {
display:none!important
}
}
