/* ----------------X-KIT ---------------- */

/* X-kit window body */
#xkit-control-panel-inner, #xkit-control-panel, #xkit-control-panel-tabs .selected, 
#xkit-extensions-panel-right-inner, #xkit-control-panel-inner, .xkit-extension:not(.selected), 
#xkit-extensions-panel-left-inner, .xkit-pokes-pc, .xkit-pokes-pc-info, #xkit-gallery-toolbar,
#xcloud-welcome-panel, #xkit-editor-sidebar, #xkit-window, .xkit-window-buttons, #xkit-editor-tabs, 
#xkit-postarchive-custom-panel-toolbar, .xkit-find-blogs-separator > div, #x1cpostage_box {
    background-color: var(--post-bg-color) !important}

#xkit-editor-area, #xkit-editor-tabs div.selected, #xkit-editor-textarea, #xkit-editor-textarea-object,
#xkit-editor-textarea-css, #xkit-editor-textarea-icon, #xkit-find-blogs-window {
    background: var(--body-bg-color) !important}

#xkit-control-panel-tabs {background-color: rgba(0,0,0,0.2) !important}

#xkit-control-panel-tabs > div:not(.selected):hover {background-color: rgba(255,255,255,0.04) !important}

#xkit-extensions-panel-left-search, #xkit-gallery-search, .xkit-window-msg input[type="text"], 
#x1cpostage_box input, #x1cpostage_box textarea, #x1cpostage_box select {
    background-color: var(--input-bg-color) !important;
    border-color: #555 !important}

#x1cpostage_box textarea {box-shadow: none !important}

#x1cpostage_box::after, #x1cpostage_box::before {
    border-top-color: #444 !important}

.xkit-extension, #xkit-extensions-panel-left, 
.xkit-extension-setting, #xkit-extensions-panel-right, #xkit-extensions-display-type-normal, 
#xkit-extensions-display-type-iconic, .xkit-extension-setting-separator, #xkit-extension-panel-slow-extension,
#xkit-extensions-panel-top, input.xkit-textbox, .xkit-pokes-menu_button, #xkit-gallery-toolbar,
.xkit-window-buttons, #xkit-editor-tabs div, #xkit-editor-tabs, #xkit-activity-plus-note-filter,
#xkit-activity-plus-note-filter > div, #xkit-postarchive-custom-panel-toolbar {border-color: #555 !important}

.xkit-extension-setting {background: none !important}

.xkit-extension:hover, #xkit-activity-plus-note-filter > div:not(.selected):hover {background-color: rgba(255,255,255,0.04) !important}

.xkit-extension.selected, #xkit-activity-plus-note-filter > div.selected {background-color: rgba(255,255,255,0.1) !important}

.xkit-pokes-sorter#reverse-toggle + label, #xkit-activity-plus-note-filter > div:not(.selected):hover {box-shadow: inset 0 0 0px 1px #444 !important}

.no-file {
    background-color: #444 !important;
    border-color: transparent !important}

/* Warnings */
#xkit-extension-panel-slow-extension, .xkit-experimental-option {
    background-color: #552 !important}

/* Buttons */
.xkit-button:not(.xkit-install-extension), .xkit-pokes-sorter#reverse-toggle + label::after, .xcloud-local-button,
.xcloud-welcome-button, .xkit-extensions-display-type-switcher:not(.selected):hover, #x1cpostage_reblog,
#x1cpostage_queue, #x1cpostage_draft, .xkit-tag-add,
#x1cpostage_replace {
    background-color: #444 !important;
    color: #ccc !important;
    border-color: #555 !important}

.xkit-button:not(.xkit-install-extension):hover, .xcloud-local-button:hover, .xcloud-welcome-button:hover,
.xkit-extensions-display-type-switcher.selected, #x1cpostage_reblog:hover,
#x1cpostage_queue:hover, #x1cpostage_draft:hover, .xkit-tag-add:hover, #x1cpostage_replace:hover {
    background-color: #555 !important}

.xkit-button:not(.xkit-install-extension):active, .xcloud-local-button:active, .xcloud-welcome-button:active,
#x1cpostage_reblog:active, #x1cpostage_queue:active, #x1cpostage_draft:active {
    background-color: #222 !important}

.xpostblockbutton {filter: invert(0.8)}

.xkit-button:not(.xkit-install-extension).disabled {background: #555 !important}

#x1cpostage_remove_caption {
    background: linear-gradient(#666, #333) !important;
    color: #ccc !important;
    border-color: transparent !important}

#x1cpostage_remove_caption:active {
    background: linear-gradient(#333, #666) !important;
    color: #ccc !important}

/* Fix for checkboxes */
.xkit-checkbox.selected b, #x1cpostage_reblog i, #x1cpostage_queue i, #x1cpostage_draft i {
    filter: invert(1) hue-rotate(180deg)}

/* Notifications */
.xkit-notification {
    background-color: rgba(0,0,0,0.7) !important;
    color: #ccc !important}

/* Text colors */
.xkit-change-ext-setting-checkbox, .xkit-extension-setting .title, #xkit-extension-enabled, 
.xkit-extension .title, .xkit-extension.text-only, .xkit-others-panel .description, .xkit-checkbox,
.xkit-window-msg, #xkit-about-window-text .thanks, #tagviewer-cpanel-notice {color: #ccc !important}

#xkit-control-panel-tabs > div:not(.selected), #xkit-editor-tabs > div:not(.selected), .xkit-activity-plus-timestamp {
    color: #aaa !important;
    text-shadow: none !important}

#xkit-control-panel-tabs .selected, .xkit-extension-setting-separator, #xkit-extensions-panel-top .title,
.xkit-extension.text-only.separator, .xkit-window-title, .no-file, #xkit-editor-tabs div.selected, #xkit-about-window-text .title,
.xkit-find-blogs-separator > div, #xkit-extension-panel-slow-extension {color: #fff !important}

#xkit-extensions-panel-top .description, .xkit-others-panel .title {color: #ddd !important}

/* Extensions install section */
.xkit-gallery-extension {
    background-color: #444 !important;
    border-color: #555 !important;
    color: #ccc !important}

.xkit-button.xkit-install-extension {border-top: none !important}

.xkit-installed-extension .xkit-button.xkit-install-extension {
    background-color: #555 !important;
    color: #aaa !important}

/* Activities modifiers */
.xkit-activity-plus-yellow-mode-on  {background-color: #440 !important}
.xkit-activity-plus-condensed-opener {
    background: rgba(255,255,255,0.1) !important;
    border-color: #666 !important;
    color: #ccc !important}

/* Find similar blogs addon */
.xkit-find-blogs-blog .m_title {color: #ccc !important}
.xkit-find-blogs-blog, .xkit-find-blogs-blog-list {border-color: #666 !important}
.xkit-find-blogs-blog:hover {background-color: rgba(255,255,255,0.1)}
}
