.morealbums ul {
    margin:10px 10px 10px 0;
}

.morealbums li {
    list-style:none;
    border-bottom:1px solid #D2B9A6;
    padding:4px;
}

.upcomingevents li {
    list-style:none;
    border-bottom:1px solid #D2B9A6;
}

.upcomingevents a {
    border:0;
}

.morealbums li a {
    display:block;
    padding:0px 0px;
