.e-textual-logo, .e-title, .e-simple-title-lineup, .e-simple-title-lineup .icon-container, .m-continuous-feed .feed-list-container .feed-list-title, .v-document-simple .m-h1-heading h1, .m-comment .comment-container h2, .e-button-mega-menu, .card-flex:hover, .card-flex:hover .container-col-text .title, .card-without-grid:hover .container-col-text .title, .card-flex:hover .container-col-text .lead, .card-without-grid:hover .container-col-text .lead p, h1 {
    color: #fff;
}
.e-btn:disabled, .e-btn.disabled, .e-btn:disabled:active, .e-btn:disabled:focus, .e-btn:disabled:hover, .e-btn.disabled:active, .e-btn.disabled:focus, .e-btn.disabled:hover, .e-menu-category {
    background-color: #000;
    border-color: #000;
    color: #999;
}
.e-menu-tab {
    background-color: #000;
    border-color: #000;
    color: #eee;
}
.e-menu-tab:hover, .e-menu-tab.is-active {
    background-color: #333;
    border-color: #000;
    color: #eee;
}
.e-btn.btn-secondary, .e-btn.btn-alternative {
    background-color: #333;
    border-color: #000;
    color: #fff;
}
.document-content-style .e-fragment, .document-content-style .framed, .document-content-style .IframeSoundcloud, .document-content-style .m-expandable-html-attachment, .document-content-style .medianet-video-container {
    background-color: #fff;
    padding: 5px;
    border-radius: 3px;
    color: #000;
}
.m-continuous-feed {
    box-shadow: 0 0.2px 5px 1px #666;
}
.s-lineup-top-story .top-story-container li {
    border-color: #222;
}
.redactionals a, .e-internal-link, .g-break .break-text-edito a, .e-anchor-link, .e-breadcrumb-link {
    color: #0191d9;
    border-color: #0191d9;
}
.redactionals a:hover, .e-internal-link:hover, .g-break:hover .break-text-edito a:hover, .e-anchor-link:hover, .e-breadcrumb-link:hover {
    color: #0191d9;
    border-color: #20b5ff;
    background: none;
}
.m-continuous-feed .feed-list-container .feed-list-date-container {
    color: #ff2626;
}
.logo svg {
    background:#666;
    padding:5px;
    border-radius:3px;
}
}

/* ==UserStyle==
@name         Tutanota Plata Noir (Unofficial) Dark theme
@namespace    github.com/NessInOnett/tutanota-plata-noir
@version      0.1.0
@license      unlicense
@preprocessor default
==/UserStyle== */

@-moz-document  domain("tutanota.com"){
  :root {
    --accent-color: #4152B4;
    --accent-color-special1: #73A873;
    --accent-color-special2: #2A2C36;
    --text-color-primary: #FFFFFF;
    --text-color-secondary: #6B6B6B;
    --bg-impact: #080808;
    --bg-primary: #181818;
    --bg-secondary: #1D1D1D;
    --bg-hover: #373737;
    --button-color-primary: var(--text-color-primary);
  }

  html, body {
    color: var(--text-color-primary);
    font-family: Roboto,-apple-system,BlinkMacSystemFont,Segoe UI,Oxygen-Sans,Ubuntu,Cantarell,Helvetica Neue,sans-serif;
    font-size: 14px;
  }

  a {
    color: var(--text-color-primary) !important;
  }
  a:hover {
    color: var(--text-color-primary) !important;
  }

  .h4 {
    font-weight: bold !important;  
  }

  button[title|="New email"],button[title|="New contact"],button[title|="New event"] {
    border: unset !important;
    border-radius: 3px !important;
    background-color: var(--accent-color);  
  }

  .button-content>.text-ellipsis {
      color: var(--text-color-primary) !important;
  }

  .text-field .text-ellipsis {
    color: var(--text-color-secondary);
  }

  .dropdown-content {
    background-color: var(--bg-impact);
    padding: 0px;
  }
  .dropdown-content .button-content:hover {
    background-color: var(--bg-hover);
  }
  small {
    color: var(--text-color-secondary);
  }

  .dialog-header {
    background-color: var(--bg-impact);
    border:0 !important;
  }
  .elevated-bg {
    background-color: var(--bg-secondary);
  }

  .active > .flex > .items-end {  /* underline underneath text fields */
    border-bottom: solid 2px var(--accent-color) !important;
  }
  .editor-border-active {
    border: solid 2px var(--accent-color) !important;
  }
  .dropdown-bar:focus {	
    border-bottom: solid 2px var(--accent-color);
  }

  .buyOptionBox button {
    background-color: var(--accent-color) !important;
  }

  /* Icons */
  .button-icon {
    color: var(--button-color-primary);
    background-color: unset !important;
  }
  .icon>svg>path {
    fill: var(--button-color-primary);
  }
  .secondary {
    color: var(--accent-color);
  }

  /* Header */
  .header-nav {
    background-color: var(--bg-impact);
    box-shadow: unset !important;
  }
  /*   Header Left */
  .logo>svg>g {
    fill: var(--text-color-primary) !important;
  }
  .logo>svg>path {
    fill: var(--accent-color) !important;
  }
  /*   Header Right */

  /* Nav bar */
  .row-selected .label {
    color: var(--accent-color) !important;    
  }
  .row-selected .label:hover {
    color: var(--accent-color) !important;
    opacity: unset !important;
  }
  .nav-button:hover {  
    color: var(--accent-color) !important;
    text-decoration: unset !important;
    opacity: unset !important;
  }
  .folder-row.row-selected {
    background-color: var(--accent-color-special2);
    border-radius: 0 25px 25px 0;
    margin-right: 0px;
  }
  .folder-column {
    background-color: var(--bg-secondary);
    border: unset !important;
  }
  .folder-counter {
    background-color: unset;
  }
  .folder-row:hover {
    color: var(--text-color-primary) !important;
    background-color: var(--bg-hover);
  }
  .folder-column > .col > .col > .justify-end > button > .b {
    color: var(--accent-color) !important;
    
  }

  /* Sidebar */
  drawer-menu {
    background-color: var(--bg-impact);
    border-right: unset;
  }

  /* Rows */
  .row-selected {
    border-color: var(--accent-color) !important;
    color: var(--text-color-primary);
  }
  .list-border-right {
    border-right: unset;
  }
  .list-row {
    background-color: var(--bg-primary);  
  }
  .list-row small {
    color: var(--text-color-secondary);
  }
  .bg-accent-fg {  /* Tutanota: please add .row-unread class for better control :( */
    background-color: var(--accent-color);
  }
  .teamLabel {
    background-color: var(--accent-color);
    color: var(--text-color-primary) !important;
  }

  /* Email header */
  .content-accent-fg {
    color: var(--text-color-secondary);
  }
  .subject {
    font-weight: bold;
  }
  .small {
    color: var(--text-color-secondary);
  }
  #mail-viewer>.header>.items-start {
    border:unset !important;
    border-left: solid 4px var(--accent-color) !important;
    border-radius: 0;
    padding-left: 0;
  }
  #mail-viewer>.header>.items-start button {
    background-color: var(--accent-color) !important;
    color: var(--text-color-primary) !important;
    padding: 6px 8px !important;
    border: 0 !important;
    border-radius: 3px !important;
  }

  /* Calendar */ 
  .content-message-bg {
    color: var(--text-color-primary);
    background-color: var(--bg-impact);
    border-radius: 3px !important;
    padding: 6px !important;
  }
  .calendar-month-week-number {
    color: var(--text-color-secondary);
    background: unset !important;
  }
  .calendar-column-border > .col > .abs {
    background-color: var(--accent-color) !important;
  }
  .calendar-hour, .calendar-day {
    border-color: var(--bg-secondary);
  }
  .calendar-hour:hover, .calendar-day:hover {
    background-color: var(--bg-hover) !important;
  }
  .calendar-alternate-background {
    background-color: var(--bg-impact) !important;
  }
  .date-current {
    color: var(--text-color-primary);
    background-color: var(--accent-color);
  }
  .date-selected {
    background-color: var(--accent-color-special1);
  }
  .calendar-event {
    background-color: var(--accent-color-special1) !important;
  }
}

/* ==UserStyle==
@name         Dark-GitLab USo-Lite
@namespace    gitlab.com/vednoc/dark-gitlab
@description  Dark and light theme for GitLab and self-hosted GitLab instances.
@author       vednoc <vednoc@pm.me> (https://gitlab.com/vednoc)
@homepageURL  https://gitlab.com/vednoc/dark-gitlab
@supportURL   https://gitlab.com/vednoc/dark-gitlab/issues
@updateURL    https://gitlab.com/vednoc/dark-gitlab/raw/master/gitlab.user.css
@version      1.3.5
@license      MIT
==/UserStyle== */

/*
 * NOTE: Modifying the code will put auto-update feature of Stylus on pause in
 * order to preserve your local changes. That means you need to manually update
 * Dark-GitLab and re-do your modifications in order to stay up-to-date.
 *
 * A way to add domains without modifying the code doesn't exist yet.
 * Relevant issue: https://github.com/openstyles/stylus/issues/307
 *
 * Make sure you have the latest version before submitting bug reports.
 */

@-moz-document domain('custom.domain'),
domain('invent.kde.org'),
domain('git.pleroma.social'),
domain('gitgud.io'),
domain('salsa.debian.org'),
domain('framagit.org'),
domain('repo.getmonero.org'),
domain('0xacab.org'),
domain('dev.gajim.org'),
domain('devel.trisquel.info'),
domain('git.cit.bcit.ca'),
domain('git.coop'),
domain('git.dev.ctu.univ-fcomte.fr'),
domain('git.drk.sc'),
domain('git.empiresmod.com'),
domain('git.fosscommunity.in'),
domain('git.immc.ucl.ac.be'),
domain('git.jami.net'),
domain('git.linux-kernel.at'),
domain('git.nzoss.org.nz'),
domain('git.silence.dev'),
domain('lab.libreho.st'),
domain('opencode.net'),
domain('skylab.vc.h-brs.de'),
domain('vbscan.fisica.unimib.it'),
regexp('^https?://(www\.)?git\.(gnu|synz)\.io(/.*)?$'),
regexp('^https?://(www\.)?code\.(videolan|briarproject)\.org(/.*)?$'),
regexp('^https?://(www\.)?source\.(small-tech|joinmastodon)\.org(/.*)?$'),
regexp('^https?://(www\.)?git\.(drupalcode|feneas|ouru|pwmt|regardscitoyens)\.org(/.*)?$'),
regexp('^https?://(www\.)?(joonpc\.skku\.edu/gitlab|mpegx\.int-evry\.fr/software)(/.*)?$'),
regexp('^https?://((next|www)\.)?(gitlab\.(?!(io|biterg)).*)(/.*)?$') {
  :root {
    --note: 'Dark-GitLab Lite v1.3.5';
    --bshadow: 0 2px 4px var(--shadow);
    --t: transparent !important;
    --avatar: 25%;
    --ui-font: 'font_name', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Noto Sans', Ubuntu, Cantarell, 'Helvetica Neue', var(--emoji-font), sans-serif;
    --mono-font: 'font_name', 'Menlo', 'DejaVu Sans Mono', 'Liberation Mono', 'Consolas', 'Ubuntu Mono', 'Courier New', 'andale mono', 'lucida console', monospace;
    --emoji-font: 'font_name', 'Apple Color Emoji', 'Segoe UI Emoji', 'Segoe UI Symbol', 'Noto Color Emoji';
    --white: #fff;
    --bb: #2c2c2c;
    --dark: #292929;
    --darken: #333;
    --darker: #424242;
    --light: #e6e6e6;
    --lighter: #aaa;
    --accent: #bf692b;
    --shadow: rgba(0,0,0,0.208);
    --yellow: #f0c674;
    --orange: #d98245;
    --red: #a54242;
    --magenta: #b294bb;
    --violet: #85678f;
    --blue: #54738c;
    --cyan: #5e8d87;
    --green: #7b823a;
    --red-darken: #4e3030;
    --red-darker: #3b2c2c;
    overflow-x: hidden;
  }
  @supports (-moz-user-select: none) {
    :root {
      --svg-status-bg: var(--dark) !important;
    }
  }
  body {
    font-family: var(--ui-font) !important;
  }
  body,
  body.navless {
    color: var(--light) !important;
    background-color: var(--dark) !important;
  }
  body::before {
    content: '';
    z-index: -1;
    position: fixed;
    top: 0;
    left: 0;
    bottom: 0;
    right: 0;
    background-color: var(--bb) !important;
  }
  body a,
  body a:hover {
    color: var(--accent);
  }
  body hr {
    border-color: var(--darker);
  }
  body h1,
  body .h1,
  body h2,
  body .h2,
  body h3,
  body .h3,
  body h4,
  body .h4,
  body h5,
  body .h5,
  body h6,
  body .h6 {
    color: var(--light);
  }
  body time {
    color: var(--lighter);
  }
  body :not(pre) > code {
    color: var(--light);
    background-color: var(--darken);
    box-shadow: inset 0 0 0 1px var(--darker);
  }
  body pre {
    color: var(--light);
    border-color: var(--darker);
    background-color: var(--darken);
  }
  body pre[class *= 'description'] {
    border-left-width: 4px;
    font-size: 0.875rem;
    color: var(--lighter) !important;
    border-color: var(--darker) !important;
  }
  body > pre:not([class]) {
    background-color: var(--t) !important;
  }
  body kbd {
    border-radius: 4px;
    color: var(--light) !important;
    background-color: var(--darken) !important;
    box-shadow: inset 0 0 0 1px var(--darker);
  }
  body .gl-link,
  body .author-link,
  .branch-commit .commit-sha {
    color: var(--accent);
  }
  body pre,
  body code,
  body kbd,
  body samp,
  body .monospace,
  body .ref-name,
  body .commit-sha,
  body .pipeline-number,
  body .label-branch,
  body table.code,
  body [class *= 'git-commit'] {
    font-family: var(--mono-font) !important;
  }
  body .container {
    border-color: var(--darker) !important;
  }
  body .danger-title,
  body .gl-field-error,
  body .invalid-feedback {
    color: var(--red) !important;
  }
  body [class *= 'price-per-'] {
    color: var(--blue) !important;
  }
  body .warning-title {
    color: var(--orange) !important;
  }
  body .valid-feedback {
    color: var(--green) !important;
  }
  body .account-well {
    color: var(--light) !important;
    border-color: var(--darker) !important;
    background-color: var(--darken) !important;
  }
  body [class *= 'two-factor-auth'] svg {
    background-color: var(--white) !important;
    box-shadow: 0 0 0 4px var(--white);
  }
  body .progress {
    background-color: var(--darker) !important;
  }
  body .progress-bar {
    color: var(--light) !important;
    border-radius: 4px;
  }
  body .progress-bar.bg-primary {
    background-color: var(--accent) !important;
  }
  body .progress.repository-languages-bar {
    background-color: var(--t) !important;
  }
  body .progress.repository-languages-bar [style *= '#375eab'] {
    background-color: #00add8 !important;
  }
  body .progress.repository-languages-bar [style *= '#776791'] {
    background-color: #000100 !important;
  }
  body .text-info:not(#z) {
    color: var(--blue) !important;
  }
  body .text-danger:not(#z) {
    color: var(--red) !important;
  }
  body .text-success:not(#z) {
    color: var(--green) !important;
  }
  body .text-warning:not(#z) {
    color: var(--orange) !important;
  }
  body .text-plain,
  .light {
    color: var(--light) !important;
  }
  body .text-secondary,
  body .text-muted,
  body .text-tertiary {
    color: var(--lighter) !important;
  }
  .cgray,
  .description-block,
  .help-block {
    color: var(--lighter) !important;
  }
  [class *= 'gl-text-gray-'] {
    color: var(--lighter) !important;
  }
  body .bg-light {
    background-color: var(--darker) !important;
  }
  body .bg-danger {
    background-color: var(--red) !important;
  }
  body .bg-secondary {
    background-color: var(--darken) !important;
  }
  body .bg-success {
    background-color: var(--green) !important;
  }
  body .bg-white,
  body .bg-gray-light {
    background-color: var(--dark) !important;
  }
  body .border-bottom,
  body .bordered-box {
    border-color: var(--darker) !important;
  }
  body .bordered-list > li,
  body .unstyled-list > li {
    border-color: var(--darker) !important;
  }
  html {
    scrollbar-color: rgba(136,136,136,0.188) rgba(136,136,136,0.082);
  }
  ::-webkit-scrollbar {
    width: 12px;
  }
  ::-webkit-scrollbar-track {
    background: rgba(136,136,136,0.082);
  }
  ::-webkit-scrollbar-thumb {
    background: rgba(136,136,136,0.188);
  }
  body ::placeholder {
    color: var(--lighter) !important;
  }
  body ::-webkit-input-placeholder {
    color: var(--lighter) !important;
  }
  body .page-wrap {
    background-color: var(--t) !important;
  }
  body .page-wrap .brand-holder p {
    color: var(--lighter) !important;
  }
  body .page-wrap hr.footer-fixed {
    padding-top: 24px;
    margin-bottom: 0;
    background-color: var(--darken) !important;
  }
  body .page-wrap hr.footer-fixed + .footer-container {
    background-color: var(--t) !important;
  }
  body .page-wrap .login-box,
  body .page-wrap .signup-box,
  body .page-wrap .omniauth-container {
    border-radius: 4px;
    border-color: var(--darker) !important;
