/* Google Fonts (SO FAR IMPOSSIBLE TO USE ROBOTO FONT)
--------------------------------------------------------------------------------*/
@font-face {
  font-family: 'Roboto' !important;
  font-style: normal;
  font-weight: 400;
  src: local('Roboto'), local('Roboto-Regular'), url(https://fonts.gstatic.com/s/roboto/v20/KFOmCnqEu92Fr1Mu4mxK.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2122, U+2191, U+2193, U+2212, U+2215, U+FEFF, U+FFFD;
}


/* ::: YOU TUBE - COMMON VAR 

/* You Tube - Loading Flickering Correction */
html[style] {
    background-color: var(--yt-rash-BGMainSectionColor) !important;
}

body {
    background: #000;
    /* font-family: 'Geneva', sans-serif !important;  (SO FAR IMPOSSIBLE TO USE ROBOTO FONT)*/
}

/* You Tube :: Master Background Control */
ytd-app[background-color-update_] {
    background: black;
}


/* YouTube - Logo Color */
path.style-scope.ytd-topbar-logo-renderer{
    fill: var(--yt-rash-YOUTUBELogoColor);
}
/* YouTube - LogoType */
#logo-icon-container.ytd-topbar-logo-renderer #youtube-paths.ytd-topbar-logo-renderer path.ytd-topbar-logo-renderer {
    fill: var(--yt-rash-YOUTUBELogoTypeColor);
}
/* YouTube - LogoType Color - Country Color */
#country-code.ytd-topbar-logo-renderer {
    color: var(--yt-rash-YOUTUBELogoTypeCountryColor);
}

/* YouTube - Icons Color including HOME TRENDING HISTORY */
yt-icon, .yt-icon-container.yt-icon {
    fill: var(--yt-rash-MainThemeColor, currentcolor);
}


/* YouTube - Button used in Expand or More */
/* Button - Color Normal */
paper-button {
    background-color: var(--yt-rash-ButtonExpanderColorNormal);
}
/* Button - Color Hover */
paper-button:hover {
    background-color: var(--yt-rash-ButtonExpanderColorHover);
}
/* Button - Text Color */
#toggle.ytd-grid-renderer {
    color: var(--yt-rash-ButtonExpanderTextColor);
}


/* :: You Tube - Dark Search Box :: */
/* SearchBox - Background */
ytd-searchbox[mode="legacy"] #container.ytd-searchbox, #container.ytd-searchbox, ytd-searchbox[mode="legacy-centered"] #container.ytd-searchbox {
    background-color: var(--yt-rash-searchbox-background);
    border: 1px solid var(--ytd-rash-searchbox-legacy-border-color);
    border-right: none;
