@-moz-document regexp("http[s]?://[^/]*swarm.*")
{

:root
{
    --swarm-primary-background: rgb(26,29,33);
    --swarm-primary-foreground: rgb(209,210,211);
    --swarm-foreground-min: rgb(34,37,41);
    --swarm-foreground-low: rgb(53,55,59);
    --swarm-foreground-high: rgb(129,131,133);
    --swarm-foreground-max: rgb(171,171,173);
    --swarm-foreground-max-20: rgba(171,171,173,0.2);
    --swarm-foreground-max-40: rgba(171,171,173,0.4);
    --swarm-primary-highlight: rgb(29,155,209);
    --swarm-secondary-highlight: rgb(242,199,68);
    --swarm-alert-background: rgb(92,78,10);
    --swarm-alert-border: rgb(147,125,16);
    --swarm-alert-error-background: rgb(73,29,29);
    --swarm-alert-error-border: rgb(146,58,58);
    --swarm-input-required-invalid: rgb(239,95,91);
}

.com { color: #696d70; }
.kwd { color: #00a7aa; }
.lit { color: #a06fca; }
.pln { color: #d1d2d3; }
.pun { color: #fc4384; }
.str { color: #e6db74; }
.typ { color: #98e342; }



body,
body.route-login
{
    background-color: var(--swarm-primary-background);
    color: var(--swarm-primary-foreground);
}



code,
pre
{
    background-color: var(--swarm-foreground-min);
    border-color: var(--swarm-foreground-low);
    color: var(--swarm-primary-foreground);
}



textarea
{
    background-color: var(--swarm-primary-background);
    border-color: var(--swarm-foreground-low);
    color: var(--swarm-primary-foreground);
}

input[type="text"],
input[type="password"]
{
    background-color: var(--swarm-foreground-min);
    border-color: var(--swarm-foreground-low);
    color: var(--swarm-primary-foreground);
}

.navbar-site li.search input,
.navbar-site li.search input:focus,
.navbar-site li.search input:valid
{
    background-color: var(--swarm-foreground-min);
}

input:focus:invalid:focus,
input:required:invalid,
select:required:invalid,
textarea:required:invalid
{
    border-color: var(--swarm-input-required-invalid);
    box-shadow: 0 0 6px var(--swarm-input-required-invalid);
}

.input-append .add-on,
.input-prepend .add-on
{
    background-color: var(--swarm-foreground-low);
    border-color: var(--swarm-foreground-low);
    color: var(--swarm-primary-foreground);
}



.table .table
{
    background-color: var(--swarm-primary-background);
}

.table th,
.table td
{
    border-color: var(--swarm-foreground-low);
}

.table th
{
    color: var(--swarm-foreground-max);
}

.table-striped tbody>tr:nth-child(odd)>th,
.table-striped tbody>tr:nth-child(odd)>td
{
    background-color: var(--swarm-foreground-min);
}

.table-hover tbody tr:hover>th,
.table-hover tbody tr:hover>td
{
    background-color: var(--swarm-foreground-low);
}

.dashboard .table-striped th
{
    border-color: var(--swarm-foreground-low);
}

.dashboard .table-striped tr.empty td
{
    background-color: var(--swarm-primary-background);
