@-moz-document domain("www.facebook.com") {
#pagelet_composer {
    height: 8000px; /*facebook from trying to load new data*/
}

div[id^="topnews_main_stream_"]{
    opacity: 0;
    pointer-events: none;
}

/* new facebook */
div[role="main"] {
    opacity: 0;
    pointer-events: none;
}

}

@-moz-document domain("pillowfort.social") {
.user-sidebar {
    background-color: #fff;
    width: 268px;
    height: 100vh;
    position: fixed;
    display: block;
    overflow-y: scroll;
}
}

