.aui-sidebar[aria-expanded="false"] ~ .aui-page-panel .ghx-controls-filters {
    padding-left: 0px;
}
.aui-sidebar[aria-expanded="false"] ~ .aui-page-panel #ghx-header {
    padding-left: 56px;
}
*/
}

@-moz-document domain("toonator.com") {
body {
    font-family: CURSIVE;
    color: #9d4dd2;
    background-color: #111;
    font-size: 62.5%;
    margin: 0;
}
.toon_preview {
    background-color: #000;
    display: inline-block;
    zoom: 1;
    width: 210px;
    margin: 3px;
    text-align: center;
    padding: 5px 0 5px 0;
    -moz-border-radius: 6px;
    border-radius: 6px;
    position: relative;
}
a.nmenu {
    color: #7621ae;
    text-decoration: none;
}
a.nmenu:hover {
    color: #7621ae;
    text-decoration: underline;
}
.toon_preview .toon_name a,
.toon_preview .toon_name_top a {
    display: block;
    font-size: 12px;
    font-weight: bold;
    color: #9d4dd2;
    text-decoration: none;
}
a.nmenu.selected {
    color: #c5a0ff;
    font-weight: bold;
}
a.foreign {
    color: #c5a0ff !important;
}
a.anonymous {
    color: #a8a8a8 !important;
}
.red,
a.red {
    color: #c5a0ff !important;
    font-weight: bold;
}
.gray {
    background: #000;
}
.toon_preview {
