.flat-theme .alert, .flat-theme .box_perfil, .flat-theme .box_perfil .capa, .flat-theme .carrinho:hover>a, .flat-theme .dados .pague, .flat-theme .finalizar, .flat-theme .lis_carrinho .concluir, .flat-theme .lis_loja .lis_produtos .acessar, .flat-theme .lis_loja .lis_produtos .comprar, .flat-theme .loja .comprar, .flat-theme .nav-tabs>li>a, .flat-theme .navbar-default .item_sub:hover>a, .flat-theme .panel, .flat-theme .well, .panel-heading {
