@-moz-document domain("uptobox.com") {
#dl {
    margin-top: -120px;
    padding: 20px;
    font-size: 26px;
}

#dl .countdown {
    font-size: 34px;
}

.add-to-account {
    display: none;
}

/* Clean navigation */
#navbar .navbar-items li a[href="https://uptobox.com/"],
#navbar .navbar-items li a[href="https://uptobox.com/my_files"],
#navbar .navbar-items li a[href="https://uptobox.com/support"],
#navbar .navbar-items li a[href="https://uptobox.com/payments"],
#navbar .lang-select {
    display: none;
}

/* Hide useless cells*/
.comparison-table > thead > tr > td:nth-child(1),
.comparison-table > thead > tr > td:nth-child(2),
.comparison-table > tbody > tr {
    display: none;
}

/* Hide premium advice */
#dl form > .red {
    display: none;
}

/* Hide signup advice */
#dl .text.errors {
    display: none;
}

/* Make button more mobile friendly */
#dl .download-btn,
#dl .big-button-green-flat {
    font-size: 36px;
    padding: 25px;
}

/* Remove clickjacking */
a[target=_blank][style="position: fixed; width: 100vw; height: 100vh; top: 0px; left: 0px; z-index: 1000;"] {
    background: blue;
    display: none;
}

/* Player full width */
.fake-player-uts {
    width: 100%;
    padding-bottom: 56.25%;
}

.fake-player-uts .vjs-big-play-button {
    width: 6em;
    height: 3em;
}

.fake-player-uts .vjs-icon-placeholder {
    font-size: 2.5em;
}
}

/* ==UserStyle==
@name           Dark Mode | E-Learning
@namespace      Cortex
@version        2.5.0
@description    Dark Mode 
@author         Cortex
@preprocessor   default
@var            color       Dark        "Dark Color"        #253447
@var            color       Darker      "Darker Color"      #1b2836
@var            color       Darkest     "Darkest Color"     #141d26
@var            color       Text-White  "Text White"        white
@var            color       Text-Grey   "Text Grey"         #8899a6
@var            color       Text-Link   "Text Hyperlinks"   #1ea1f2
==/UserStyle== */
@-moz-document domain("e-learning.tuhh.de")
{
    /*
  :root
  {
    --Dark: #253447;
    --Darker: #1b2836;
    --Darkest: #141d26;
    --Text-White: white;
    --Text-Grey: #8899a6;
    --Text-Link: #1ea1f2;
  }
    */
    *
    {
        font-family: "Montserrat" !important;
    }

    /* -- Backgrounds -- */
    #layout_wrapper #flex-header, #layout_wrapper > div.sticky-wrapper div, .sidebar .sidebar-widget-header, #layout_footer, select, #tabs a, td.toolbar, td.printhead, textarea, section.course-statusgroups article section table tfoot tr td, table.blank, td.blank, td.onlineinfo, td.blanksmall, #layout_container, .message_body, .studip-widget .widget-header, #notification_marker, article.studip header, table.default > tfoot > tr > td, .action-menu .action-menu-content, #blubber_threads > .thread > .contextinfo, #blubber_threads .comments .posting, #blubber_threads .comments, #blubber_threads li.more, form.default fieldset > legend, .grid-stack-item-content .widget-content, #globalsearch-searchbar #globalsearch-results article > header, #globalsearch-searchbar #globalsearch-results article mark, #tabss, #barBottomContainer, #tabs li
    {
        background: var(--Dark) !important;
    }

    th, div.messagebox_info, section.contentbox header, table td.printcontent, #layout_content > div, footer, blockquote, .ui-widget-content, .content_seperator, .content_seperator td, #index .messagebox, #notification_container *, .grid-stack-item-content .widget-header, .grid-stack-item-content .widget-actions, #forum .posting
    {
        background: var(--Darker) !important;
    }

    body, #layout_page > div, #layout_page > div > div, input, .sidebar, html, #tabs li.current a, blockquote > .author, .ui-state-default, .ui-menu-item .ui-state-default, .ui-widget-content .ui-state-default, .ui-widget-header .ui-state-default, table.dates .nextdate, table#schedule_data thead tr td, table#schedule_data tbody td:first-child, #page_title_container, .helpbar, #layout_page, .tooltip .tooltip-content, #globalsearch-searchbar #globalsearch-list, #tabs li.current
    {
        background: var(--Darkest) !important;
    }

    /* -- Text -- */
    *, body, table.default > caption
    {
        color: var(--Text-Grey) !important;
    }

    a, a:link, a:visited
    {
        color: var(--Text-Link) !important;
    }

    select, input
    {
        color: var(--Text-White) !important;
    }

    /* -- Buttons -- */
    button, a.button, button.accept.button, .opengraph
    {
        border: 1px solid #1da1f2 !important;
        background: var(--Darkest) !important;
        color: #1da1f2 !important;
        border-radius: 100px !important;
        padding: 4px 12px !important;
        -webkit-transition: background 200ms linear, border 200ms linear !important;
        -ms-transition: background 200ms linear, border 200ms linear !important;
        transition: background 200ms linear, border 200ms linear !important;
    }

    button:hover, a.button:hover, button.accept.button:hover, .opengraph:hover
    {
        background: var(--Dark) !important;
        border: 1px solid black !important;
        color: #1da1f2 !important;
        border-radius: 100px !important;
        padding: 4px 12px !important;
    }

    select, #globalsearch-searchbar #globalsearch-input
    {
        border-color: var(--Text-Link);
        border-radius: 10px;
        padding-left: 10px;
    }
}

@-moz-document domain("e-learning.tuhh.de")
{
    /* - Individual - */
    div.messagebox_info, div.messagebox_success
    {
        background-color: var(--Dark);
    }

    #tabs
    {
        padding: 0;
    }

    #tabs li a img
    {
        display: inline-block;
        list-style-type: none;
    }

    #tabs li.current a img
    {
        margin-left: 2px;
    }

    #tabs, #layout_wrapper #flex-header #barTopMenu, div.index_main
    {
        text-align: center !important;
        margin-left: auto !important;
        margin-right: auto !important;
        background: var(--Dark);
    }

    #tabs li
    {
        padding: 0 3px 0 3px;
    }

    #layout_wrapper #flex-header #barTopMenu li
    {
        margin: 0 6px 3px 6px;
    }

    #tabs2 > li.current:after, #tabs > li.current:after
    {
        background-color: red;
    }

    .quicksearchbutton
    {
        z-index: 1;
    }

    table.default > caption
    {
        text-align: center !important;
        font-weight: 700;
    }

    input
    {
        height: 20px !important;
        font-size: 13px !important;
        padding-left: 5px !important;
    }

    .quicksearchbutton
    {
        border: 1px solid #1da1f2 !important;
        background: var(--Darkest) !important;
        color: #1da1f2 !important;
        border-radius: 100px !important;
        padding: 1px 5px !important;
        -webkit-transition: background 200ms linear, border 200ms linear !important;
        -ms-transition: background 200ms linear, border 200ms linear !important;
        transition: background 200ms linear, border 200ms linear !important;
    }

    .quicksearchbutton:hover
    {
        background: var(--Dark) !important;
        border: 1px solid black !important;
        color: #1da1f2 !important;
        border-radius: 100px !important;
    }

    tr:nth-child(odd), #MediaLinks > ul > li:nth-child(odd)
    {
        background: var(--Dark);
    }

    tr:nth-child(odd), #MediaLinks > ul > li:nth-child(even)
    {
        background: var(--Darker);
    }

