    {
        background: #ff7f50;
    }

    table td.printhead1, table td.printhead4, table td.printhead3, table td.printhead2
    {
        background: linear-gradient(rgba(15, 84, 128, 1), rgba(15, 84, 128, 1)), url(https://e-learning.tu-harburg.de/studip/assets/images/content_object_arr-down.png);
        border: 0;
        border-radius: 5px;
    }

    #MediaLinks > ul > li > div
    {
        padding: 0px 0px 0px 20px !important;
        line-height: 18px;
    }

    #notification_marker, #header_avatar_menu .action-menu-icon, #page_title_container, #tabs, #barBottomContainer, #globalsearch-searchbar #globalsearch-input
    {
        border-color: transparent;
        border: transparent;
    }

    #globalsearch-searchbar
    {
        color: white !important;
    }

    body:not(.fixed) #barTopMenu > li > a .navtitle, body:not(.fixed) #barTopMenu > li > label .navtitle
    {
        opacity: 1 !important;
    }

    body:not(.fixed) #barTopMenu > li:nth-child(2)
    {
        padding-left: 15px;
        padding-right: 15px;
    }

    body:not(.fixed) #barTopMenu > li:nth-child(3)
    {
        padding-left: 20px;
        padding-right: 20px;
    }

    article.studip
    {
        border-color: transparent;
    }

    #layout_content
    {
        max-width: 2400px;
    }

    /* Sidebar */
    .sidebar, .sidebar:before
    {
        border: 0px solid transparent;
    }

    .sidebar .sidebar-widget
    {
        border: 1px solid transparent !important;
        background: transparent !important;
        padding: 10px 5px !important;
    }

    .sidebar .sidebar-widget-header
    {
        text-align: center;
        border-radius: 20px 20px 0 0;
        color: var(--Text-White) !important;
    }

    #layout-sidebar > section > div > div.sidebar-widget-content > ul > li
    {
        border: 1px solid #1da1f2 !important;
        background: var(--Darkest) !important;
        color: #1da1f2 !important;
        border-radius: 100px !important;
        padding: 2px 12px !important;
        margin: 5px 0px !important;
        text-align: center;
        -webkit-transition: background 200ms linear, border 200ms linear !important;
        -ms-transition: background 200ms linear, border 200ms linear !important;
        transition: background 200ms linear, border 200ms linear !important;
    }

    #layout-sidebar > section > div > div.sidebar-widget-content > ul > li:hover
    {
        background: var(--Dark) !important;
        border: 1px solid black !important;
    }

    .widget-links.sidebar-views > li.active, .widget-links.sidebar-navigation > li.active
    {
        box-shadow: none;
    }

    .widget-links.sidebar-views > li.active:after
    {
        border: 9px solid rgba(255, 255, 255, 0);
        border-left-color: #fff;
    }

    .widget-links.sidebar-navigation > li.active:after
    {
        border: 9px solid rgba(255, 255, 255, 0);
        border-left-color: #fff;
    }

    #layout_content > section
    {
        border: transparent;
    }

    /* Login Screen */
    #layout_wrapper > div:nth-child(3) > div > table > tbody > tr > td
    {
        text-align: center;
    }

    #layout_wrapper > div:nth-child(3) > div > table > tbody > tr > td > form > table
    {
        padding: 0px 0px 0px 220px;
    }

    /* Das Help Bild Dark */
    a.helpbar .helpbar-title
    {
        background: /* top, transparent black, faked with gradient */
        linear-gradient(rgba(0, 0, 0, 0.6), rgba(0, 0, 0, 0.6)), /* bottom, image */
        url(https://e-learning.tu-harburg.de/studip/assets/images/icons/white/question-circle.svg);
        -webkit-background-size: 24px 24px;
        -moz-background-size: 24px 24px;
        -o-background-size: 24px 24px;
        background-position: right top;
        background-repeat: no-repeat;
        margin: 4px 4px -2px;
        overflow: hidden;
        padding: 2px 0 10px 6px;
    }

    /* Startsetite Bild Dark*/
    div.index_container div.index_main
    {
