    }

    /* - Do not Display - */
    a.helpbar-container
    {
        visibility: hidden;
    }

    div#layout-sidebar div.sidebar-image img, .sidebar-title, .sidebar-context, .helpbar, #layout_wrapper #flex-header #barTopStudip, #barBottomLeft, #barTopFont
    {
        display: none;
    }

