        padding-top: 1px;
    }

    dl dt:nth-child(1)
    {
        padding-bottom: 1px;
        padding-top: 10px;
    }

    #blubber_threads > .thread, #blubber_threads .posting, #blubber_threads .posting *, #blubber_threads .posting > .writer, #threadwriter > .row, form.default fieldset > legend, form.default fieldset, .grid-stack-item-content
    {
        border-color: var(--Darker);
    }

    #layout_wrapper #layout_page #layout_container
    {
        margin: 0 auto;
    }

    #current_page_title
    {
        text-align: center;
        font-weight: 800;
    }

    img[src*="https://e-learning.tuhh.de/studip/assets/images/icons/black/staple.svg"]
    {
        filter: /* for demonstration purposes; originals not entirely black */
        contrast(1000%)/* black to white */
        invert(100%)/* white to off-white */
        sepia(100%)/* off-white to yellow */
        saturate(10000%)/* do whatever you want with yellow */
        hue-rotate(10deg);
    }

    img[src*="https://e-learning.tuhh.de/studip/assets/images/icons/black/outbox.svg"]
    {
        filter: /* for demonstration purposes; originals not entirely black */
        contrast(1000%)/* black to white */
        invert(100%)/* white to off-white */
        sepia(100%)/* off-white to yellow */
        saturate(100000000%)/* do whatever you want with yellow */
        hue-rotate(290deg);
    }

    .opengraph-area
    {
        max-width: 400px;
        max-height: 100px !important;
    }

    .opengraph .image
    {
        width: 60px;
        height: 60px;
        margin-left: 20px;
    }

    #forum .posting
    {
        margin-left: 10px;
    }

    #header_avatar_menu .action-menu-content
