        right: -124px;
    }

    #globalsearch-list
    {
        border: 50px !important;
        border-color: black !important;
    }

    #globalsearch-list *
    {
        border: 0px !important;
    }

    #barBottomContainer
    {
        top: 0;
        order: 1;
        position: relative;
        justify-content: center;
    }

    #layout_wrapper
    {
        padding-top: 0;
    }

    #layout_wrapper #flex-header
    {
        order: 0;
    }

    #layout_page
    {
        order: 1;
    }

    #layout_footer
    {
        order: 3;
    }
}

