        margin-bottom: 5px !important;
    }
    .borderwrap .ipbtable .catend {
        display: none !important;
    }
    /* topic message user information */
    .borderwrap .ipbtable tbody tr:nth-of-type(2) td:nth-of-type(1) .postdetails center {
        font-size: 9px;
        line-height: 12px;
        transform: scale(1.1);
    }
    /* topic message user reputation */
    .borderwrap .ipbtable tbody tr:nth-of-type(2) td:nth-of-type(1) .postdetails a:nth-last-child(-n+3):hover img {
        animation: postdetails-zoom .1s;
        transform: scale(1.5);
    }
    /* topic message user avatar */
    .borderwrap .ipbtable .postdetails center .user-avatar {
        display: block;
        height: 40px;
        position: relative;
    }
    .borderwrap .ipbtable .postdetails center .user-avatar img {
        left: 50%;
        position: absolute;
        top: 7px;
        transform: translateX(-50%);
    }
    .borderwrap .ipbtable .postdetails center .user-avatar img {
        height: 45px;
        max-height: unset !important;
        max-width: unset !important;
        transition: all .1s ease;
    }
    .borderwrap .ipbtable .postdetails center .user-avatar:hover img {
        animation: avatar-zoom .1s;
        height: 150px;
    }
    /* topic message user menu button */
    .borderwrap .ipbtable tbody tr:nth-of-type(1) td:nth-of-type(1) {
        position: relative;
    }
    .borderwrap .ipbtable tbody tr:nth-of-type(1) td:nth-of-type(1) .popupmenu-new {
        position: absolute !important;
        top: 26px !important;
        transform: translateX(100px) !important;
    }
    /* topic message buttons bottom panel */
    .borderwrap .ipbtable tbody tr:nth-of-type(3) {
        display: none !important;
    }
    .borderwrap .ipbtable:hover tbody tr:nth-of-type(3) {
        display: table-row !important;
    }


    /* topic message floating buttons panel */
    .borderwrap .ipbtable tbody tr:nth-of-type(3) {
        box-shadow: 0 0 5px 1px /*[[highlight-color]]*/ !important;
    }
    /*[[postbuttons-position]]*/


    .borderwrap .ipbtable tbody tr:nth-of-type(3) .formbuttonrow:nth-of-type(1) {
        width: 170px;
    }
    .borderwrap .ipbtable tbody tr:nth-of-type(3) .formbuttonrow:nth-of-type(2) {
        width: 450px;
    }
    .borderwrap .ipbtable tbody tr:nth-of-type(3) .formbuttonrow:nth-of-type(2) div:nth-of-type(3) {
        float: right;
        position: relative;
    }
    .borderwrap .ipbtable tbody tr:nth-of-type(3) .formbuttonrow:nth-of-type(2) div:nth-of-type(3) .popupmenu {
        position: absolute;
        left: unset !important;
        right: 13px !important;
        top: -50px !important;
    }
    /* whole topic message */
    .borderwrap .ipbtable tbody {
        box-shadow: none !important;
        filter: brightness(90%) !important;
    }
    .borderwrap .ipbtable:hover tbody {
        box-shadow: 0 0 5px 1px /*[[highlight-color]]*/ !important;
        filter: brightness(100%) !important;
    }
    /* topic message buttons and links highlighting */
    .borderwrap .ipbtable a:hover,
    .borderwrap .ipbtable .formbuttonrow a.g-btn:hover {
        filter: drop-shadow(0 0 2px /*[[highlight-color]]*/) !important
    }
    /* reply section */
    #qr_open {
        height: /*[[qr_open-height]]*/px;
    }
    #qr_open #qr-form-default {
        bottom: 0 !important;
        position: fixed !important;
        width: 98%;
    }
    #qr_open #ed--1_textarea {
        resize: vertical !important;
    }

    /* animations */
    @keyframes postdetails-zoom {
        from {
            transform: scale(1);
        }
        to {
            transform: scale(1.5);
        }
    }
    @keyframes avatar-zoom {
        from {
            height: 55px;
        }
        to {
            height: 150px;
        }
    }
}

/*
The following CSS variables define colorblind-friendly styling for github.com
*/

@-moz-document domain("github.com") 
{
    /* Main color substitutions */
    :root {
       --green-subs: #648FFF;
       --green-subs-light: #8AABFF;
       --green-subs-lighter: #D5E3F9;
       --red-subs: #FFB000;
       --red-subs-light: #FFD371;
       --red-subs-lighter: #FFEEBA;
       --ibm-2: #785EF0;
       --ibm-3: #DC267F;
       --ibm-4: #FE6100;
    }

    /* GENERAL*/
    .text-green {
       color: var(--green-subs)!important;
    }

    .text-red {
       color: var(--red-subs)!important;
    }

    /* DIFF */
    .blob-code-deletion {
       background-color: var(--red-subs-lighter);
     }

    .blob-num-deletion {
       background-color: var(--red-subs-light);
    }

    .blob-code-deletion .x {
       background-color: var(--red-subs-light);
    }

    .blob-code-addition {
       background-color: var(--green-subs-lighter);
    }

    .blob-num-addition {
       background-color: var(--green-subs-light);
    }

    .blob-code-addition .x {
       background-color: var(--green-subs-light);
    }

    .diffstat-block-added {
       background-color: var(--green-subs);
    }

    .diffstat-block-deleted {
       background-color: var(--red-subs);
    }

    /* DANGER*/
    .btn-danger {
       color: var(--red-subs);
    }

    .btn-danger:hover, [open]>.btn-danger {
       background-color: var(--red-subs);
    }

    .Box--danger, .Box--danger .Box-body:last-of-type, .Box--danger .Box-row:first-of-type {
       border-color: var(--red-subs);
    }


    /* ISSURES and PULL REQUESTS*/
    .open.octicon {
       color: var(--green-subs);
    }

    .closed.octicon, .reverted.octicon {
       color: var(--red-subs);
    }

    /* INSIGHTS*/
    /* Pulse */

    .summary-stats li .octicon-issue-opened {
       color: var(--green-subs);
    }

    .summary-stats li .octicon-issue-closed {
       color: var(--red-subs);
    }

    .summary-stats li .octicon-git-branch {
       color: var(--green-subs);
    }

    .authors-and-code .insertions {
       color: var(--green-subs);
    }

    .authors-and-code .insertions {
       color: var(--green-subs);
    }

    .authors-and-code .deletions {
       color: var(--red-subs);
    }

    /* Code frequency */
    .cadd {
       color: var(--green-subs);
    }
    .code-frequency .addition {
       fill: var(--green-subs);
    }

    .cdel {
       color: var(--red-subs);
    }

    .code-frequency .deletion {
       fill: var(--red-subs);
    }
}

