}

.l-excerpt__title {
	font-weight: 500;
	height: auto;
}

.l-excerpt__title .c-link {
	display: inline-block;
	line-height: 1.4em;
}

/* market */
.l-wrap>div[itemprop="articleBody"] {
	background-color: rgba(255, 255, 255, .5);
}

.l-wrap>div[itemprop="articleBody"] .l-cols {
	padding: 3mm;
}

.c-iconBox__input {
	padding: 5px 3mm 5px 10mm
}

.c-iconBox__icon {
	top: 10px
}

.l-separationBg {
	width: calc(100% - 6mm);
	min-height: 30vh;
	padding: 3mm
}

.c-toggleTrigger--searchBar,
.l-searchBarToggle {
	background-color: rgba(255, 255, 255, .3)
}

.c-toggleTrigger--searchBar {
	border-radius: 2mm 2mm 0 0;
	margin: 0 1mm
}

.l-searchBarToggle {
	border-radius: 3mm
}

.u-gapBottom--sm,
.u-gapBottom--lg {
	overflow: hidden
}

.u-gapBottom--lg {
	margin: 0 0 5mm
}

.l-chosen .u-gapBottom--lg {
	background: #ccc
}

/* event */
.l-selectedThumb--specialTopic {
	background-size: cover;
	border-radius: 1.5mm;
	box-shadow: 0 0 0 1mm #aaa
}

.u-gapBottom--lg .l-selectedList {
	background-color: #e6e3dd;
	padding: 0 3mm
}

.c-selectItem {
	padding: 6px 0
}

/* footer */
.l-footer {
	position: absolute;
	left: 0;
	bottom: 0;
	width: 100vw;
	box-shadow: 0 0 2mm #888
}

.l-footerLinks {
	font-size: 0;
	display: block;
	text-align: center
}

.l-footerLinks>li {
	display: inline-block;
	margin: 0
}

.l-footer__copyright .list>span>a>img {
	margin-right: 1mm;
}

.u-gapRight--max {
	margin-right: 10mm;
}

/* round corners */
.c-mainCard,
.u-gapBottom--lg:first-child,
.u-gapBottom--sm,
.c-panel_content,
.c-dropdown__select ul,
#_popIn_img3_pc ._popIn_recommend_art_img {
