@-moz-document regexp('http(s?)://www\\.ecosia\\.org/.*') {
/* General "body" color properties */
body,
div.results-page-stretcher > div, 
div.results-page-stretcher > nav, 
div.results-page-stretcher > main,
.results-page-stretcher {
	background: #0c0a01;
    color: white;
}

/* Homepage elements */
.search-section .content {
	background: #0c0a01 !important;
    color: white;
}
.dropdown {
	background: #0c0a01 !important;
    color: #353535;
}
.list-item:hover, .dropdown-item:hover { background: #ece4c9; }

/* Forms */
@media only screen and (max-width: 991px) {
	.search-form-field { 
		background: #0c0a01 !important;
		color: white;
	}
}
.search-form, .search-form-field, .input { background: #ece4c9 !important; }

/* Top section's btns/icons */
@media only screen and (max-width: 991px) {
	div.results-page-stretcher > nav { background: #ece4c9; }
}
.search-tree-counter-button,
.search-form-addon-button { 
    background: #ece4c9;
    color: black;
}
.search-header > .search-header-menu-group button.pill {
	background: #908870;
    border-color: transparent;
}
.search-header > .search-header-menu-group button.pill:hover { 
    background: #483c1a; 
    border-color: #104e26;
    color: white;
}
.search-header > .search-header-menu-group button.pill svg > path { fill: #104e26 !important; }
.search-header > .search-header-menu-group button.pill:hover svg > path { fill: white !important; }

/* Nav links */
li.navbar-item-active .navbar-link::after { background-color: #00644d !important; }
li.navbar-item-active .navbar-link path { fill: #00644d; }

/* Vertical bar above results list */
@media only screen and (min-width: 992px) {
    .navbar-row { border-bottom: 2px solid #104e26; }
}

/* Top result right card */
.entity-block {
	background: #908870;
    color: black;
}

/* Results on mobile */ 
@media only screen and (max-width: 991px) {
	.card-mobile {
		background: #4a4846;
    	color: black;
	}
}

/* Result titles */
div.results-page-stretcher > * a { color: #908870; }
div.results-page-stretcher > * a:visited { color: gray; }
div.results-page-stretcher > * a:hover { color: #104e26; }

/* Text under result title */
.result-snipped { color: #668a69; }

/* Video result cards */
.thumbnail-video .thumbnail-details {
	background: #908870;
    color: black;
}
.thumbnail-video .thumbnail-details .thumbnail-title,
.thumbnail-video .thumbnail-details .thumbnail-publisher { color: #003c05; }
.thumbnail-video .thumbnail-details time { color: #353535; }

/* Page image, grid border */
.image-result { border: 2px solid transparent; }

/* Footer */
footer { background-color: #353535 !important; }
}

