@-moz-document domain("radio.yandex.ru") {
/* sorry if im create bugs. im new in codding */

/* create BG style. Here you can install any color or gradien in you choise */

    .background,
    .page-root__content,
    .button-radio-slider,
    .theme-white .deco-shade {
        background-image: linear-gradient(320deg, #FF057C 0%, #8D0B93 50%, #321575 100%);
    }

/* change text style  */

    .page-station__title,
    .player-controls__title,
    .player-controls__artists,
    .track__info,
    span {
        color: white !important;
    }

    a.link {
        color: gray !important;
    }

    a.link:hover {
        color: black !important;
    }


/* fix (delete) overflow-y */

    .page-root {
        overflow-y: hidden;
    }

/*  PopUp restyle */

    .theme-white .deco-pane-popup,
    .volume__holder {
        background: #00000085;
        box-shadow: 5px 5px 30px 0px black;
    }

    .theme-white .deco-button .deco-button-stylable:hover,
    .d-select__item_selected:hover,
    .theme-white .deco-popup-menu__item:hover
    .button-radio-slider__selector{
        background: #666;
    }

    .active:hover,
    .button-radio-slider__item:hover {
        background: #808080 !important;
    }

    .volume__holder {
        visibility: hidden;
    }
    
    .button-radio-slider {
        background: none;
    }

/* delete unnecessary ellements im equalizer */

    .theme-white .d-equalizer__popup,
    .theme-white .volume__bar,
    .theme-white .deco-popup-menu__item-group,
    .theme-white .d-equalizer__button,
    .volume__btn,
    .theme-white .deco-button .deco-button-stylable {
        background: none;
        box-shadow: none;
    }


/* Nav-bar restyle */

    .nav__level {
        background: none;
    }

    .nav__item:hover {
        background: #00000085;
    }

    .body_state_pushed .d-logo {
        visibility: hidden;
    }

    .nav__content .d-logo{
        visibility: visible;
        
    }
    
    .body_state_pushed .nav__level {
        background: #FFFFFF7A;
    }
    .nav__item_selected {
        background: #666;
    }
}
