@-moz-document url("https://www.pillowfort.social/"), url("https://www.pillowfort.social/posts/new"), url("https://www.pillowfort.social/communities"), url("https://www.pillowfort.social/block_list"), url("https://www.pillowfort.social/about"), url("https://www.pillowfort.social/donations"), url("https://www.pillowfort.social/generate_invites"), url("https://www.pillowfort.social/notifs_dash"), url("https://www.pillowfort.social/settings"), url("https://www.pillowfort.social/followers"), url("https://www.pillowfort.social/following"), url("https://www.pillowfort.social/mutuals"), url-prefix("https://www.pillowfort.social/community/"), url-prefix("https://www.pillowfort.social/messages/"), url-prefix("https://www.pillowfort.social/search/"), url-prefix("https://www.pillowfort.social/posts/") {
/*@import url('https://fonts.googleapis.com/css2?family=Raleway:ital,wght@0,100;0,200;0,300;0,400;0,500;0,600;0,700;0,800;0,900;1,100;1,200;1,300;1,400;1,500;1,600;1,700;1,800;1,900&display=swap');*/

/*
Custom colours - Feel free to define your own!
I recommend this site to find some nice ones:
https://dudleystorey.github.io/thenewdefaults/
*/
:root {
	--main-color:/*[[main-col]]*/; /*Default: Crepe*/
		/*Used for the main background, links, post/avatar/comment borders*/
	--sub-color-1:/*[[sub-col-1]]*/; /*Default: Strawberry*/
		/*Used for link hovers, the Followers button, pagination, and tab hovers*/
	--sub-color-2:/*[[sub-col-2]]*/; /*Default: Punch*/
		/*Used for the Following button, submit buttons/sliders, and additional button outlines on the donation page*/
	--sub-color-3:/*[[sub-col-3]]*/; /*Default: Bubblegum*/
		/*Used for the Mutuals button*/
	--sub-color-4:/*[[sub-col-4]]*/; /*Default: Rosewood*/
		/*Used for submit button/slider hovers*/
}

/*Body, link and sidebar colours*/
body {
	background-color:var(--main-color);
	/*font-family: 'Raleway', 'Source Sans Pro', 'Sans Serif';*/
}
a, .sidebar a, .new-post .post-type-container .post-type.selected, .edit-post .post-type-container .post-type.selected, .pagination>li>a {
	color:var(--main-color);
}
a:hover, .sidebar a:hover, .navbar-inverse .navbar-brand:hover, .navbar-inverse .navbar-text a:hover {
	color:var(--sub-color-1);
}
.sidebar-boxes .sidebar-box.followers {
	color:var(--sub-color-1);
}
.sidebar-boxes .sidebar-box.following {
	color:var(--sub-color-2);
}
.sidebar-boxes .sidebar-box.mutuals {
	color:var(--sub-color-3);
}
div.post.main, .avatar img, .main {
	border-color:var(--main-color);	
}
.btn-primary, .btn-info, .pagination>.active>a {
	background-color:var(--sub-color-2);
	border-color:var(--sub-color-2);
}
.btn-primary:hover, .btn-info:hover, .btn-outline-info:hover, .pagination>.active>a:hover {
	background-color:var(--sub-color-4);
	border-color:var(--sub-color-4);
}
.btn-outline-info {
	color:var(--sub-color-2);
	border-color:var(--sub-color-2);
}
.nav-tabs {
	border: none;	
}
#communitiesListCtrlId #comm-index-nav-tabs li>a:hover, #notifs-tabs li>a:hover {
	color:var(--sub-color-1);
}
}

