@-moz-document domain("news.ycombinator.com") {
html {
    width: 100%;
}

body {
    background: #222;
    margin: 20px auto;
    width: 70%;
}

#hnmain {
    border-radius: 5px;
    overflow: hidden;
    background: #1F2430;
}

.age {
    color: #ccc !important
}
.c00 {
    color: #ccc !important
}
.comhead > a {
    color: #dedede !important
}
.comhead {
    color: #dedede !important
}
.hnuser {
    color: #ccc !important
}
.subtext > a:link {
    color: #ccc !important
}
.subtext > a {
    color: #ccc !important
}
.subtext {
    color: #ccc !important
}
.title > a {
    color: #dedede !important
}
a:link {
    color: #ccc !important
}

.rank {
    margin-right: 5px;
}

.itemlist {
    width: 95%;
    margin: 20px auto;
}

.title a {
    color: white;
    font-size: 16px;
}

tr.spacer {
    height: 30px !important;
}

.subtext {
    margin-top: 8px;
    font-size: 13px;
}

.subtext,
.subtext span,
.subtext a {
    font-size: 13px;
}

.spacer {
    height: 10px !important;
}

#hnmain > tbody > tr:nth-child(1) > td > table {
    background-color: #b51515 !important;
    padding: 16px 8px !important;
}
#hnmain > tbody > tr:nth-child(1) > td > table > tbody > tr > td:nth-child(1) {
    display: none;
}

b, p, td, tr, span, a, #hnmain > tbody > tr:nth-child(4) a:link {
    color: #ccc !important;
    font-size: 14px;
    font-family: Verdana;
}

.comment-tree tr table {
    margin: 8px 0 !important;
}

}

