/*Lists*/
.items-section .body .content-row>ul>li.text-field .frame-content .view-mode ol, 
.items-section .body .content-row>ul>li.text-field .frame-content .view-mode ul li ul,
.markdown ol,
.markdown ul li ul {
    margin-bottom: .5em;
}

.items-section .body .content-row>ul>li.text-field .frame-content .view-mode ul li,
.items-section .body .content-row>ul>li.text-field .frame-content .view-mode ol li,
.markdown ul li,
.markdown ol li {
    list-style: none !important;
    margin-left: -4px;
    margin-bottom: .4em;
}

.items-section .body .content-row>ul>li.text-field .frame-content .view-mode ul li ul,
.markdown ul li ul {
    margin-top: .4em;
    margin-left: 8px;
}

.items-section .body .content-row>ul>li.text-field .frame-content .view-mode ol li ol,
.markdown ol li ol {
    margin-top: .4em;
    margin-left: 2px;
}

.items-section .body .content-row>ul>li.text-field .frame-content .view-mode ol,
.markdown ol {
    counter-reset: section;
}

.items-section .body .content-row>ul>li.text-field .frame-content .view-mode ul li:before,
.markdown ul li:before {
    content: "•";
    transform: scale(2.25);
    position: relative;
    display: inline-block;
    top: -3px;
    left: -11px;
    color: rgba(var(--podio-blue-rgb), 1);
    opacity: .3;
    margin-left: -5px;
}

.items-section .body .content-row>ul>li.text-field .frame-content .view-mode ul li ul li:before,
.markdown ul li ul li:before {
    content: "→";
    transform: scale(1);
    position: relative;
    display: inline-block;
    top: 0;
    left: -11px;
    color: rgba(var(--podio-blue-rgb), 1);
    opacity: .4;
    margin-left: -13px;
}

.items-section .body .content-row>ul>li.text-field .frame-content .view-mode ul li ul li ul li:before,
.markdown ul li ul li ul li:before {
    content: "•";
    transform: scale(2.25);
    position: relative;
    display: inline-block;
    top: -3px;
    left: -11px;
    color: var(--gray-500);
    opacity: .5;
    margin-left: -14px;
}

.items-section .body .content-row>ul>li.text-field .frame-content .view-mode ul li ul li ul li ul li:before,
.markdown ul li ul li ul li ul li:before {
    content: "→";
    transform: scale(1);
    position: relative;
    display: inline-block;
    top: 0;
    left: -11px;
    color: var(--gray-500);
    opacity: .7;
    margin-left: -14px;
}

.items-section .body .content-row>ul>li.text-field .frame-content .view-mode ol li:before,
.markdown ol li:before {
    counter-increment: section;
    content: counters(section, ".") " ";
    transform: scale(1);
    position: relative;
    display: inline-block;
    font-weight: 800;
    letter-spacing: .05em;
    font-size: .9em;
    top: 0px;
    left: -10px;
    color: var(--gray-500);
    opacity: .7;
    margin-left: -5px;
}

