.items-card>.scrollable>table>tbody .card-group-heading:first-child {
    /*display: none;*/
}


.items-card>.scrollable>table>tbody .card-group-heading td {
    height: 2.7em;
    text-transform: uppercase;
    font-size: 1.2rem;
    letter-spacing: .05em;
    color: var(--gray-600);
    font-weight: 600;
}

.items-card>.scrollable>table>tbody .card-group-body>td {
    padding: 12px 12px;
    border-right: 6px solid #fff;
    background: #F4F6F8;
    border-bottom: 0;
}
.items-card>.scrollable>table>tbody .card-group-body>td.first {
    border-left: 6px solid #fff;
}

.items-card>.scrollable>table>tbody .card-group-heading td .text {
    left: 16px !important;
}

.items-card>.scrollable>table thead {
    visibility: hidden;
}

.header-col, .items-card>.scrollable .floating .floating-scrollable .inner>div, .items-card>.scrollable>table>thead tr th {
    border-right: 6px solid #fff;
    padding: 8px 8px 5px 14px;
}

.items-card>.scrollable .floating .floating-scrollable .inner>div {
    border-radius: 12px 18px 0 0;
    box-shadow: inset 0 1px 0 rgba(0,0,0,.04), inset 0 -1px 0 rgba(0,0,0,.04), inset 1px 0 0 rgba(0,0,0,.04), inset -1px 0 0 rgba(0,0,0,.04);
}

.header-col.first, .items-card > .scrollable .floating .floating-scrollable .inner > div.first, .items-card > .scrollable > table > thead tr th.first {
    margin-left: 3px;
}

.money-field-value-component.size-small {
    margin-bottom: 4px;
    font-size: 1.2rem;
    font-weight: 500;
    color: var(--gray-500);
    letter-spacing: .05em;
}

/* Card contacts */

.card-component>section .avatar-component {
    border: 2px solid var(--gray-100);
    margin-right: -1rem;
    width: 26px;
    height: 26px;
}

.card-component>section .contacts-field-value-component .contacts-field-name {
    margin-left: 1.5rem;
}

/* Setas de navegação de colunas no kanban */

div.navigation-arrow:nth-child(1) {
  display: none;
}

div.navigation-arrow:nth-child(2) {
  display: none;
}

/* Indent nos títulos de colunas do kanban */
.header-col.first, .items-card > .scrollable .floating .floating-scrollable .inner > div.first, .items-card > .scrollable > table > thead tr th.first {
  text-indent: 0;
}

.items-card > .scrollable .floating {
  background-color: #fff;
}

.items-card>.scrollable .floating .floating-scrollable .inner>div:after {
    border-right: 8px solid #fff;
    position: relative;
    z-index: 5;
}

.items-card>.scrollable>table {
    border: 0;
}

/*Cards "Badge"*/
.app-badge-layout-component .badge-component {
    box-sizing: border-box;
    border: 2px solid var(--gray-300);
    border-radius: var(--theme-radius);
    background-color: var(--gray-100);
}

.badge-component:hover {
    border-color: var(--gray-400);
    box-shadow: unset;
    -webkit-transform: unset;
    transform: unset;
    background-color: var(--gray-200);
}

.badge-component .app-badge-content {
    background: var(--gray-100);
    border-top: 1px solid var(--gray-200);
    border-bottom: 1px solid var(--gray-200);
}

.badge-component>main, 
.badge-component>section {
    border-top: 1px solid var(--gray-200);
    background-color: transparent;
    
}

.badge-component:hover>main, 
.badge-component:hover>section,
.badge-component:hover>footer{
    border-top: 1px solid transparent;
    background-color: transparent;
}

.badge-component .app-badge-header {
    font-family: var(--font-stack-sans);
    background-color: white;
    border-radius: var(--btn-radius) var(--btn-radius) 0 0;
}

.small-text-field-value-component p {
    line-height: 1.4;
}

.badge-component>section .contacts-field-value-component {
    margin-bottom: 16px;
}

.badge-component>section .avatar-component {
    border: 2px solid var(--gray-100);
    margin-right: -1rem;
    width: 26px;
    height: 26px;
    box-shadow: 0 1px 5px rgba(0,0,0,.10);
}

.badge-component>section .contacts-field-value-component .contacts-field-name {
    margin-left: 1.5rem;
}

.badge-component>section .contacts-field-value-component .contacts-field-extra-contacts {
    background-color: white;
    color: var(--gray-600);
    font-weight: 700;
    padding: 0 6px;
    margin-left: 1.5rem;
    border-radius: var(--btn-radius);
    box-shadow: 0 1px 3px rgba(0,0,0,.1);
    font-size: .9em;
    line-height: 26px;
}

.badge-component>footer {
    border-top: 1px solid transparent;
    transition: unset;
    background-color: var(--gray-100);
    border-radius: 0 0 var(--btn-radius) var(--btn-radius);
}

.category-field-value-component:after {
    width: 8px;
    background: linear-gradient(to right,rgba(255,255,255,0) 0,var(--gray-200) 100%);
}

.large-text-field-value-component {
    background: white;
}

/*Progress bar*/
.progress-field-value-component {
    border-radius: calc(var(--btn-radius)/1.5);
    background: var(--gray-300);
}
.progress-field-value-component__progress {
    background: var(--gray-600);
    border-radius: calc(var(--btn-radius)/1.5);
    opacity: .8;
}

.progress-field-value-component__progress[data-progress="100"] {
    background: var(--green);
}

.card-component>main .progress-field-value-component {
    margin: 0 13px;
    border: none;
}


/* ////// Table ////// */




.items-table .field-type-state-item {
    padding: 1px 8px;
    border-radius: var(--pill-radius);
}

.items-table table tr.even:hover>td, .items-table table tr.odd:hover>td, .items-table table tr.ui-selected>td, .items-table table tr.ui-selecting>td, .items-table table tr:hover>td {
    background-color: var(--gray-100);
}


/* ////// Baloon ////// */


.simple-balloon,
.balloon-inner {
    box-shadow: 0 12px 28px -4px rgba(0,0,0,0.2) !important;
    background: #fff;
    border: 2px solid var(--gray-400) !important;
    border-radius: var(--theme-radius) !important;
    min-width: 160px;
}

.simple-balloon .balloon-inner {
    box-shadow: none !important;
    background: #fff;
    border: none !important;
    min-width: unset !important;
}

.balloon.settings-balloon>.arrow {
    border-bottom-color: var(--gray-400);
}

.balloon.below .arrow-border {
    top: 3px;
}

.balloon.settings-balloon .balloon-inner .buttons {
    background-color: var(--gray-200);
    border-radius: 0 0 var(--btn-radius) var(--btn-radius);
}

.simple-balloon.arrow-left.gravity-n:after {
    border-width: 9px;
    border-bottom-color: var(--gray-100);
    left: 32px;
    margin-left: -10px;
}

.simple-balloon.arrow-left.gravity-n:before {
    border-width: 12px;
    margin-left: -14px;
}

.simple-balloon .balloon-content {
    padding: 12px !important;
}

.simple-balloon .balloon-content li:hover {
    background: var(--gray-200);
    border-radius: var(--btn-radius);
}

.app-filters-popover .main {
    margin: -12px;
    height: 100%;
}

.app-header-layout-switcher-options ul.app-settings-dropdown-list li .icon-checked {
    position: absolute;
    right: 12px;
}

ul.app-settings-dropdown-list li {
    margin-left: -4px;
    margin-right: -4px;
    height: 22px;
    line-height: 20px;
    padding: 2px 2px 2px 6px;
}


