@-moz-document domain("mcbbs.net") {
/* 整体设置 */
:root{
    --bgcolor:/*[[bgcolor]]*/;
    --bgimg:url(/*[[bgimg]]*/);
    --bgsize:/*[[bgsize]]*/
}
html:not([stylus-iframe]) body,#body_fixed_bg{
    background-image:var(--bgimg);
