  {
    background-color: #ECEBEC;
  }

  .rbx-btn-primary-lg:hover, .rbx-btn-primary-lg:focus
  {
    background-color: #ECEBEC;
  }

  .avatar-back
  {
    background-image: url(https://wallpapercave.com/wp/wp5275300.jpg);
  }

  .notification-blue
  {
    background-color: #000000;
  }

  body
  {
    background-color: #000000;
    color: #000000;
    font-family: serif;
    font-weight: 300;
