             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, 0.3);
             border-radius: 2px;
             background-color: #212121;
        }
        html[hide-scrollbar="true"] ::-webkit-scrollbar{display:none}
        ::-webkit-scrollbar {
             width: 5px;
             background-color: #212121;
        }
        ::-webkit-scrollbar-thumb {
             border-radius: 2px;
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, .3);
             background: linear-gradient(-225deg, #6441a5 0%, #8D0B93 50%, #321575 100%);
        }

body {
    background: #151515;
}
.clipr-card.card, .clipr-info-card.card, .card {
    background: #191919;
}
#clip_url, .clipr-textbox {
    background: #252525 !important;
    color: #fff;
}
* {
    color: #fff;
}
.footer > .container.text-center:after {
    content: 'Dark Theme Made by blyad';
    color: #6c757da6;
}
.blog-text, .blog-text-md {
    color: #306384 !important;
}
.page_speed_1455266486 {
    color: #3668a4 !important;
}
}

@-moz-document domain("lichess.org") {
.is3d cg-board::before {
    background-image: url("https://i.imgur.com/WLLDtsJ.png") !important;
  }
square.last-move {
    will-change: transform;
    background-color: rgba(238,144,22,0.5);
}
square.move-dest {
    background: radial-gradient(rgba(85,60,20,0.5) 19%, rgba(0,0,0,0) 20%);
    pointer-events: auto;
}
square.oc.move-dest {
    background: radial-gradient(transparent 0%, transparent 80%, rgba(85,60,20,0.3) 80%);
}
square.selected {
    background-color: rgba(85,60,20,0.5);
}
}

