}
/*Buttons and bits and pieces found at the top*/
 #qpReturnLobbyVoteContainer {
    box-shadow: 0px 0px 15px 1px #cc2525;
}
#qpVoteReturnYes.selected {
    background-color: #b85cb8;
}
#qpVoteReturnNo.selected {
