}
.qpAvatarListBar.completed, .qpAvatarListBar.looted {
    background-color: #8f2644;
}
.qpAvatarListBar.watching {
    background-color: #b0392d;
}
.qpAvatarAnswerNumber {
    background-color: #8a1515;
}
/*Report buttons, up- and downvote buttons*/
 #qpFeedbackInput.glow {
