}

/*In the off chance someone is using this with a guest account, make sure the notice is clearly visible*/
#guestAccountNoticeContainer{
    left: 43%;
    bottom: 45px;
    background-color: rgba(35, 35, 35, 0.75);
    border-color: rgba(10, 10, 10, 0.5);
}
#guestAccountNoticeContainer > h4 > a{
    color: rgba(251, 131, 108, 0.85);
}
}

@-moz-document domain("gammal.rollspel.nu") {
:root {
    --color-white: #fff;
    --color-black: #121212;
    --color-dark-blue: #033145;
    --color-light-blue: rgb(211, 236, 252);
    --color-light-blue-transparency: rgba(40, 171, 255, 0.2);
    --color-primary: rgb(28, 97, 145);
    --color-link: var(--color-primary);
    --color-hover: #2379B5;
    --color-dark-grey: #656A6D;
    --color-grey: rgb(189, 189, 189);
    --color-light-grey: #f5f5f5;
    --color-warning: #a20000;
    --color-notice: #FCF7D7;
    --color-text: #fff;
    
    --spacing: 1rem;
    --spacing-quarter: 0.25rem;
    --spacing-half: 0.5rem;
    --spacing-double: 2rem;
