    filter: sepia(1) hue-rotate(172deg) saturate(1);
}

.b-post__footer li {
    display: flex;
    align-items: center;
    padding: 0rem 0.5rem !important;
}

.b-post__footer .js-post-controls {
 	position: absolute;
    padding: 0px;
    margin: 0px !important;
    height: 1rem;
}

.b-post__timestamp,
.b-post__footer .js-post-controls {
    opacity: 0.75;    
}


.js-post__content-wrapper:hover .b-post__timestamp,
.js-post__content-wrapper:hover .js-post-controls{
    opacity: 1.0;
}

.conversation-content > ul > li:nth-child(2n-1) .b-post__footer .js-post-controls {
    bottom: 0px;
    left: 80px;
    transform: translateX(-53%);
}

.conversation-content > ul > li:nth-child(2n) .b-post__footer .js-post-controls {
    top: 80px;
    right: 80px;
    transform: translateX(46%);
}

/* DISCUSSION STICKY STICKER */

.b-post__footer ul.js-post-info {
    position: absolute;
    top: 2rem;
    left: 4rem;
}


/* ARROW; SPEECH BUBBLE */

.OLD__post-content {
 	padding: 0px;
    margin-bottom: -2px;
}

.conversation-content > ul > li .js-post__content-wrapper::before {
    content: '';
	position: absolute;
    border-width: 9px;
    border-style: solid;
}

.conversation-content > ul > li:nth-child(2n-1) .js-post__content-wrapper::before {
    bottom: 32px;
	left: 160px;
    transform: translateX(-100%);
    border-color: transparent #bdbdbd transparent transparent;
}

.conversation-content > ul > li:nth-child(2n) .js-post__content-wrapper::before {
    top: 32px;
	right: 160px;
    transform: translateX(100%);
    border-color: transparent transparent transparent #bdbdbd;
}

/* SPEECH BUBBLE */

.b-post__body {
    position: static;
 	background-color: #fff !important;
    border: 2px solid #bdbdbd;
    border-radius: 20px;
    padding: 1rem !important;
}

.b-post--first .b-post__title {
 	position: absolute;
    top: 1rem;
    left: 4rem;
    font-size: 18px;
}

.b-post__content .js-post__content-text {
    line-height: 20px;
}

.b-post__content ul {
    margin-left: 1.5em;    
}

.b-post__content li {
    padding: 0px !important;
}

.conversation-content > ul > li:nth-child(2n-1) .b-post__body {
    border-left-width: 1px;
}

.conversation-content > ul > li:nth-child(2n) .b-post__body {
    border-right-width: 1px;
}


/* BLOCK QUOTES */

.bbcode_container {
    margin: 1rem -1rem;
    margin-bottom: 0.75rem;
	border-bottom: 1px dashed #000;
    line-height: 12px;
}

.js-post__content-text .bbcode_container:first-child {
    border-top-left-radius: 20px;
	border-top-right-radius: 20px;
    margin-top: -1rem;
}

.js-post__content-text .bbcode_container:not(:first-child) {
	border-top: 1px dashed #000;
}

.bbcode_quote {
    margin-bottom: 0px !important;
	background-color: transparent !important;
	font-size: 11px;
}

/* ROUNDED IMAGES */

.conversation-content > ul > li img {
	border-radius: 7px;
}



/* LAST EDITED NOTICE */
.conversation-content .b-post__edit.OLD__post-edit {
    position: absolute;
    bottom: 1px;
    right: 280px;
    font-size: 10px;
    margin: 0px;
}

/* GET LINK TO POST */

.b-post__count {
 	position: absolute;
    bottom: 0px;
    right: calc(160px + 1rem);
    visibility: hidden;
}

.b-post__count::before {
    display: block;
    position: absolute;
    visibility: visible;
 	content: 'Länk till inlägg';
    white-space: nowrap;
    transform: translateX(-90%);
}

/* PHOTO GALLERY */
.photo-preview, .gallery-details {
    float: none;
    display: inline-block;    
}

.photo-preview {
    padding-top: 1rem;
}

.photo-preview > .b-gallery-thumbnail-list__item {
    padding-bottom: 0px;
}

.gallery-details {
    vertical-align: bottom;
    padding-bottom: 7px;
}


/* CREATE NEW ANSWER */

.b-content-entry.js-content-entry:not(.js-content-entry-edit-post) {
 	position: fixed !important;
    left: 0px;
    right: 0px;
    bottom: 0px;
    z-index: 100;
    padding: 1rem;
    background-color: #fff;
    border-top: 1px solid #bdbdbd;
}

.b-content-entry .js-toolbar-secondary {
    float: left;
}

.b-content-entry .js-toolbar-secondary li {
    width: 44px;
    height: 44px;
    position: relative;
    border: 0px none;
    border-right: 1px solid #C8CCCE;    
    margin: 0px;
    background: none;
}

.b-content-entry .b-toolbar {
    position: relative;
}

.b-content-entry .b-toolbar li:hover {
    background-color: #D3ECFC !important;
}

.b-content-entry .js-toolbar-secondary li[data-panel="b-content-entry-panel__content--toggle-editor"] {
    position: absolute;
    right: 0;
    top: 0;
    z-index: 20;
    border: 0px none;
    background-color: #f6fbf9;
    box-shadow: -3px 0px 10px -5px #ccc;
}

/* CREATE A NEW DISCUSSION */

.new-content-widget::before {
    content: 'Skapa en ny diskussion';
    display: block;
    width: 100%;
    padding-top: 2rem;
    padding-left: 1rem;
    font-size: 24px;
    font-weight: bold;
}

.new-content-widget::after {
    content: 'I detta tema är inläggsrutan längst ned på sidan. Tänk på att följa forumets regler med att det är schysst att vara viktig, men ännu viktigare att vara schysst.';
    display: block;
    font-size: 12px;
    padding: 0.5rem 1rem;
}

/* REMOVED BUTTONS */

.b-content-entry li[data-panel="b-content-entry-panel__content--link"], /* Add link */
.b-content-entry .cke_button__pastefromword,                            /* Paste from word */
.b-content-entry .cke_button__link,                                     /* Create link */
.b-content-entry .cke_button__unlink,                                   /* Remove link */
.b-content-entry .cke_toolbar_separator,                                /* Menu separator */
.b-content-entry .cke_button__undo,                                     /* Undo */
.b-content-entry .cke_button__redo,                                     /* Redo */
.b-content-entry #cke_41.cke_toolbar,                                   /* Paste CODE */
.b-content-entry .cke_button__vbtable,                                  /* Insert table */
.b-content-entry .cke_button__horizontalrule                            /* Horizontal line */
{
    display: none;
}
}
