    background-image: url(https://i.postimg.cc/7hVJVg6J/BKnight.png) !important;
}
.is2d .black.bishop {
    background-image: url(https://i.postimg.cc/yY23TY6P/BBishop.png) !important;
}
.is2d .black.king {
    background-image: url(https://i.postimg.cc/BZcF4GgZ/BKing.png) !important;
}
.is2d .black.queen {
    background-image: url(https://i.postimg.cc/Hs6784Rf/BQueen.png) !important;
}
