@-moz-document domain("nitrotype.com") {
body {
    overflow-y: scroll;
    background: none;
    overflow-x: hidden;
    position: relative;
}

html {
    background: fixed url("https://wallpapershome.com/images/pages/pic_h/15659.jpg") no-repeat, #2d2e2e;
    box-sizing: border-box;
    font-family: "Comic Sans MS";
    font-size: 16px;
    font-weight: 300;
    line-height: 1.2;
    background-size: 100%;
    background-position: center;
}
.dash-copyContainer {
    background: #0d0d0d;
    border-radius: 2px;
    flex: 1;
    overflow: hidden;
    padding: 15px;
    display: flex;
    border: none;
}

.dash-copy {
    color: #fff;
    font-family: "Comic Sans MS";
    font-size: 25px;
}

::selection {
    background-color: #fff;
    color: #000;
}
}

@-moz-document url-prefix('https://www.brick-hill.com/') {

body {
    background:        #202020 /*[[LS93D]]*/ repeat;
    -webkit-animation: /*[[NV9EK]]*/ 225s linear infinite;
    -moz-animation:    /*[[NV9EK]]*/ 225s linear infinite;
    -o-animation:      /*[[NV9EK]]*/ 225s linear infinite;
    animation:         /*[[NV9EK]]*/ 225s linear infinite;
    color:             #fff;
}
@-webkit-keyframes BackgroundAnimation {
    from {
        background-position: top left;
    }
    to {
        background-position: bottom right;
    }
}
@-moz-keyframes BackgroundAnimation {
    from {
        background-position: top left;
    }
    to {
        background-position: bottom right;
    }
}
@-o-keyframes BackgroundAnimation {
    from {
        background-position: top left;
    }
    to {
        background-position: bottom right;
    }
}
@keyframes BackgroundAnimation {
    from {
        background-position: top left;
    }
    to {
        background-position: bottom right;
    }
}

