@-moz-document regexp("https?://(?!(mail.google.com|cloud2.zoolz.com)).*") {
/* Darker Scrollbar for Google Chrome by Zigboom Designs */


::-webkit-scrollbar {
	width:10px;
	height:10px;
}

::-webkit-scrollbar-thumb {
	background-color:#333333 !important;
	border-radius:5px;
    box-shadow: 0 0 1px #fff inset !important;
}

::-webkit-scrollbar-thumb:hover{
	background-color:#444444 !important;
    box-shadow: 0 0 1px #fff inset, 0 0 1px rgba(255,255,255,0.5) inset !important;
}

::-webkit-scrollbar-track:vertical {
    margin-top: 3px;
    margin-bottom: 3px;
}

::-webkit-scrollbar-track:horizontal {
    margin-left: 3px;
    margin-right: 3px;
}



/* Corner */

::-webkit-scrollbar-corner {
    background: transparent;
}


/* Button - Vertical */
 
::-webkit-scrollbar-button:vertical:start {
    width: 10px;
    height: 8px; 
    border-left: 5px solid transparent;
    border-right: 5px solid transparent;
    border-bottom: 5px solid #575757;
}

::-webkit-scrollbar-button:vertical:start:hover {
    border-bottom: 5px solid #777;   
}

::-webkit-scrollbar-button:vertical:end {
    width: 10px; 
    height: 8px; 
    border-left: 5px solid transparent;
    border-right: 5px solid transparent;
    border-top: 5px solid #575757;
}

::-webkit-scrollbar-button:vertical:end:hover {
    border-top: 5px solid #777;
}

::-webkit-scrollbar-button:start:increment, 
::-webkit-scrollbar-button:end:decrement {
   display: none;
}


/*  Button - Horizontal */


::-webkit-scrollbar-button:horizontal:start {
    width: 8px; 
    height: 10px; 
    border-top: 5px solid transparent;
    border-bottom: 5px solid transparent;
    border-right: 5px solid #575757;
}

::-webkit-scrollbar-button:horizontal:start:hover {
    border-right: 5px solid #777;
}

::-webkit-scrollbar-button:horizontal:end {
    width: 8px; 
    height: 10px; 
    border-top: 5px solid transparent;
    border-bottom: 5px solid transparent;
    border-left: 5px solid #575757;
}

::-webkit-scrollbar-button:horizontal:end:hover {
    border-left: 5px solid #777;
}
}

