@-moz-document url-prefix("https://kogama.com.br"), url-prefix("https://www.kogama.com") {
/* 
Created by: MB_LIthium 
Stylus - Firefox

V.0.0.3
*/

:root {
/* Mude as cores por aqui dos principais elementos. Faça sua própria combinação! :) */
    --color-hr: #2f3640; /* header elements */
    --bg-theme: #353b48; /* Cor 2 */
    --bg-timeline: #353b48; /* Background da Timeline do Perfil */
    --xp-progress: #2ed573; /* Progress XP */
    --xp-bar: #7bed9f; /* XP Bar */
    /* --chat-send --chat-reply */
}
/* Header principal */
html body#root-page-mobile header#pageheader {
    width: 90%;
    margin-top: 50px;
    margin-left: 50px;
    margin-right: 50px;
    background-color: var(--color-hr);
    position: absolute;
    display: block;
    border-radius: 20px;
}
/* SubHeader principal */
html body#root-page-mobile div#content.authenticated div#content-container header#pagesubheader {
    margin-top: 50px;
    display: block;
    margin-left: 50px;
    margin-right: 50px;
    width: 90%;
    background-color: var(--bg-theme);
}

/* XP BAR */
.xp-bar .progress {
    background-color: var(--xp-bar);
}
.xp-bar .progress .progression-bar {
