@-moz-document url-prefix("https://laravel.com/docs/") {
body {
  color: #eeeeee;
}
body h1,
body h2,
body h3,
body h4,
body h5,
body h6,
body button,
body input,
body optgroup,
body select,
body label,
body textarea {
  color: #eeeeee;
}
body .docs_main h4 a {
  color: #eeeeee;
}
body .docs_main h3 a {
  color: #eeeeee;
}
body .docs_main h2 a {
  color: #eeeeee;
}
body .docs_main ul:not(:first-of-type) li {
  color: #eeeeee;
}
body .docs_main .content-list ul li {
  color: #eeeeee;
}
body .docs_main h1 + ul li a {
  color: #eeeeee;
}
body p {
  color: #e0e4e8;
}
body ::placeholder {
  /* Chrome, Firefox, Opera, Safari 10.1+ */
  color: #e0e4e8;
}
body :-ms-input-placeholder {
  /* Internet Explorer 10-11 */
  color: #e0e4e8;
}
body ::-ms-input-placeholder {
  /* Microsoft Edge */
  color: #e0e4e8;
}
body .input_group.search input[type=submit] {
  -webkit-filter: invert(0.6);
  filter: invert(0.6);
}
body .custom_select select {
  background: #242424;
}
body .custom_select::after {
  -webkit-filter: invert(0.6);
  filter: invert(0.6);
}
body .sidebar_layout {
  background-color: #1b1b1b;
}
body .callout {
  background: #242424;
  box-shadow: 0 20px 30px -16px rgba(9, 9, 16, 0.2);
}
body footer {
  background: -webkit-gradient(linear, left bottom, left top, color-stop(85%, #1b1b1b), to(#242424));
  background: linear-gradient(to top, #1b1b1b 85%, #242424);
}
body .sidebar_layout .sidebar {
  background: -webkit-gradient(linear, left bottom, left top, from(#1b1b1b), to(#242424));
  background: linear-gradient(to top, #1b1b1b, #242424);
}
@media (min-width: 65em) {
  body .sidebar_layout .sidebar::before {
    background: -webkit-gradient(linear, left bottom, left top, from(#1b1b1b), to(#242424));
    background: linear-gradient(to top, #1b1b1b, #242424);
  }
}
body .sidebar_layout .sidebar nav .navigation_contain ul li h2 {
  color: #e0e4e8;
}
body .sidebar_layout .sidebar nav .navigation_contain ul li a {
  color: #eeeeee;
}
body footer .footer_bg {
  background: #242424;
}
body .footer_nav .footer_nav_contain ul li a {
  color: #e0e4e8;
}
body .partner_block {
  background: #1b1b1b;
}
body .footer_info .social_links li img {
  -webkit-filter: invert(0.6);
  filter: invert(0.6);
}
body code[class*=language-],
body pre[class*=language-] {
  color: #f8f8f2;
}
body :not(pre) > code[class*=language-],
body pre[class*=language-] {
  background: #333333;
}
body .token.comment,
body .token.prolog,
body .token.doctype,
body .token.cdata {
  color: #8292a2;
}
body .token.punctuation {
  color: #f8f8f2;
}
body .token.property,
body .token.tag,
body .token.constant,
body .token.symbol,
body .token.deleted {
  color: #f92672;
}
body .token.boolean,
body .token.number {
  color: #ae81ff;
}
body .token.selector,
body .token.attr-name,
body .token.string,
body .token.char,
body .token.builtin,
body .token.inserted {
  color: #a6e22e;
}
body .token.operator,
body .token.entity,
body .token.url,
body .language-css .token.string,
body .style .token.string,
body .token.variable {
  color: #f8f8f2;
}
body .token.atrule,
body .token.attr-value,
body .token.function,
body .token.class-name {
  color: #e6db74;
}
body .token.keyword {
  color: #30bced;
}
body .token.regex,
body .token.important {
  color: #fd971f;
}
body .line-numbers .line-numbers-rows {
  border-right: 1px solid #999;
}
body .line-numbers-rows > span:before {
  color: #999;
}
}

