@-moz-document domain("youtube.com") {
/* suki edits */

/* navbar logo fix */
.exp-invert-logo.inverted-hdpi #header::before,
.exp-invert-logo.inverted-hdpi .ypc-join-family-header .logo,
.exp-invert-logo.inverted-hdpi #footer-logo .footer-logo-icon,
.exp-invert-logo.inverted-hdpi #yt-masthead #logo-container .logo,
.exp-invert-logo.inverted-hdpi #masthead #logo-container,
.exp-invert-logo.inverted-hdpi .admin-masthead-logo a,
.exp-invert-logo.inverted-hdpi #yt-sidebar-styleguide-logo #logo {
    background: no-repeat url(https://i.imgur.com/jtiQHQD.png);
    background-size: 100px 30px;
}

/* upload button fix */
#upload-btn {
    margin-right: 0;
    padding-right: 0;
    padding-top: 2px;
}

/* featured video menu button spacing */
.c4-inline-welcome-form > form:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(5) > button:nth-child(1) {
    margin-right: 5px;
}

/* button-background contrast */
.yt-uix-button-default, .yt-uix-button-shelf-slider-pager, .yt-uix-form-input-select, .yt-uix-form-input-checkbox-element, .yt-uix-button-subscribe-unbranded {
    background-color: var(--cdyt-cell-bg-color) !important;
}

.yt-uix-expander button,
.yt-uix-expander button:hover {
    background-color: transparent !important;
}

/* comment expander fixes + fixing blue colours on various elements */
.comment-replies-renderer-expander-down .load-more-text,
.read-more .yt-uix-button-content,
.comment-renderer-like-count {
    color: var(--cdyt-text-link) !important;
    border-color: var(--cdyt-text-link) !important;
}

.comment-replies-renderer-expander-down::after {
    filter: invert(100%);
}

/* autoplay fixes */
.yt-uix-checkbox-on-off label {
    background: var(--cdyt-cell-bg-color);
}

.checkbox-on-off label {
    color: var(--cdyt-text-secondary);
}

.yt-uix-hovercard-card-body {
    background: var(--cdyt-bg-color-medium);
}

.yt-uix-hovercard-card-border {
    border: 1px solid var(--cdyt-button-color) !important;
}

.yt-uix-clickcard-card-reverse .yt-uix-card-body-arrow-vertical, .yt-uix-hovercard-card-reverse .yt-uix-card-body-arrow-vertical {
    border-bottom-color: var(--cdyt-bg-color-medium) !important;
}

/* channel page settings button fix */
.casual-settings-button:hover,
.casual-settings-button {
    background-color: transparent !important;
}

/* search bar fixes */
.sbqs_c:hover,
.sbqs_a:hover,
.gsfs:hover {
    background: var(--cdyt-button-color) !important;
}

.sbdd_b {
    border-color: var(--cdyt-menu-bg-color) !important;
}

/* fix [time] ago text being blue */
.comment-renderer-time .yt-uix-sessionlink {
    color: var(--cdyt-text-secondary) !important;
}

/* fix orange comment likes */
#comment-section-renderer .sprite-like[aria-checked="true"],
#comment-section-renderer .sprite-like[aria-checked="true"]::before {
    -webkit-filter: invert(1);
    filter: invert(1);
}

/* fix colours on video author comments */
.comment-renderer-author-comment-badge.creator .comment-author-text {
    color: white !important;
}

.comment-renderer-author-comment-badge.creator {
    background-color: var(--cdyt-text-link) !important;
}

/* dark text fixes */
.exp-invert-logo li.guide-section h3,
.exp-invert-logo li.guide-section h3 a {
    color: var(--cdyt-text-secondary);
}

.branded-page-related-channels h3 a,
.branded-page-related-channels h3 {
    color: var(--cdyt-text-main);
}

.c4-featured-content-editor .c4-featured-content-notes {
    color: var(--cdyt-text-main);
}

.empty-shelf-text {
    color: var(--cdyt-text-secondary);
}

.empty-upsell-messages h2 {
    color: var(--cdyt-text-main);
}

.empty-upsell-messages p {
    color: var(--cdyt-text-secondary) !important;
}

#comment-section-renderer .sprite-comment-actions::before {
    opacity: 0.7 !important;
}

/* suki edits end */
}

