@-moz-document url-prefix("https://lichess.org/simul/") {
    .game-list {
        margin-top: 450px !important;
        position: relative;
    }
    .host {
        padding: 0 !important;
        position: absolute;
        top: -400px;
        left: 50%;
        transform: translateX(-50%);
        width: 304px; /* multiple of 8 */
        height: auto;
        box-shadow: 0 0 0 7px rgba(214,79,0,0.5) !important;
    }
}

@-moz-document domain("www.erepublik.com") {
#ListCampaignsController #ListCampaignsBattles.wars .war_card {
    width: 100%;
    height: 40px;
    display: flex;
    margin-bottom: 5px;
}

#ListCampaignsController #ListCampaignsBattles.wars .war_card .card_top {
    display: flex;
    width: 100%;
    height: auto;

}

#ListCampaignsController #ListCampaignsBattles.wars .war_card .card_bottom {
    width: 10%;
    bottom:auto;
    height: auto;
}

#ListCampaignsController #ListCampaignsBattles.wars .war_card .card_bottom .fight{
    width: 100%;
}

#ListCampaignsController #ListCampaignsBattles.wars .war_card .card_bottom .fight .fight_btn {
    position: relative;
    bottom: auto;
    width: 200px;
    height: auto;
    background: none;
    filter: invert();
}

#ListCampaignsController #ListCampaignsBattles.wars .war_card .card_bottom .fight .fight_btn img {
    width:30px;
    height: 30px;
}

#ListCampaignsController #ListCampaignsBattles.wars .war_card .card_bottom .fight .timer {
    color: #f00;
}

#ListCampaignsController #ListCampaignsBattles.wars .war_card .card_top .war_title {
    width:350px;
}

#ListCampaignsController #ListCampaignsBattles.wars .war_card .card_top .war_flags {
    display: flex;
    width: auto;
    position: relative;
    height: auto;
    transform: scale(0.2);
    left:-35px;
}

#ListCampaignsController #ListCampaignsBattles.wars .war_card .card_top .war_flags .war_type {
    transform: scale(2);
    left:-250px;
    top: 1px;
}

.war_type::before {
    display:none;
}

.war_type > img[src="//www.erepublik.net/images/icons_svg/military/war_listing/default_war_type.svg"]{
    display:none;
}

.hexagons > div > img[src="//www.erepublik.net/images/icons_svg/military/war_listing/tanks.svg"]{
    display:none;
}

.hexagons > div > img[src="//www.erepublik.net/images/icons_svg/military/war_listing/hexagon.svg"]{
    display:none;
}

.attacker_points > img[src="//www.erepublik.net/images/icons_svg/military/war_listing/attacker.svg"]{
    display: none;
}

.defender_points > img[src="//www.erepublik.net/images/icons_svg/military/war_listing/defender.svg"]{
    display: none;
}


#ListCampaignsController #ListCampaignsBattles.wars .war_card .card_top .allies {
    height: auto;
    width: 15em;
    background: none;
}

#ListCampaignsController #ListCampaignsBattles.wars .war_card .card_top .war_title span {
    white-space: nowrap;
}

#ListCampaignsController #ListCampaignsBattles.wars .war_card .card_top .war_flags .black_bar {
    background: none;
    transform: scale(5);
    width: auto;
}

#ListCampaignsController #ListCampaignsBattles.wars .war_card .card_top .war_flags .hexagons {
    left:-520px;
    top: 1px;
    transform: scale(5); 
}

#ListCampaignsController #ListCampaignsBattles.wars .war_card .card_top .war_title span {
    font-weight: bold;
}

#ListCampaignsController #ListCampaignsBattles.wars .war_card .card_top .war_title span b {
    display:none;
}

#ListCampaignsController #ListCampaignsBattles.wars .war_card .card_top .war_flags .black_bar .attacker_points, #ListCampaignsController #ListCampaignsBattles.wars .war_card .card_top .war_flags .black_bar .defender_points{
    width: 60px;
    height: 20px;
}



.war_title::before{
    content: ""; width: 5px; height: 28px; background-color: #283b4d; position: absolute; left: 3px;
} 

#ListCampaignsController #ListCampaignsBattles.wars .war_card .card_top .war_title span{
    margin-left: -9px;
}
}

