@-moz-document domain("trello.com") {
/* font size (cards etc) */
    html, body, input, select, textarea {font-size: 15px !important;font-family:segoe ui;} /* instead of 14px */
    body {zoom:.95;}

    /**************** HEADER *****************/

    /* header bar */
    #header {background: rgba(0,0,0,.15) !important;}

    /* header buttons */
    .header-btn.header-notifications, .header-btn.header-boards, 
    .header-btn.header-search, .header-btn.header-login, 
    .header-btn.header-signup {
        background: #ffffff45 !important;
        }
