/*         border-bottom: 1px solid #ddd !important; */
        border-radius: 4px !important;
        padding: 6px 8px 5px;
        margin-bottom: 10px;
        box-shadow:0px 0px 0px 1px #0000001a, 0px 1px 3px 0px #0000000f;
		background-color:white !important;
