/* ==UserStyle==
@name         Material Dark GitHub
@version      2.9
@description  A Material Dark theme for GitHub
@namespace    CharlieEtienne
@author       CharlieEtienne
@homepageURL  https://github.com/CharlieEtienne/material-github
@supportURL   https://github.com/CharlieEtienne/material-github/issues
@updateURL    https://raw.githubusercontent.com/CharlieEtienne/material-github/master/material-github.user.css
@license      MIT
==/UserStyle== */
@-moz-document domain("github.com") {
:root{
    /*[[theme]]*/

    --selected:  hsl(174, 38%, 66%);
    --selected2: hsl(174, 38%, 55%);

    --btn:  hsl(174, 38%, 40%);
    --btn2: hsl(174, 29%, 33%);
    --btn3: hsl(174, 21%, 22%);
    --btn4: hsl(174, 25%, 15%);
}

textarea:focus {
    background-color: var(--bg1) !important;
}
body {
    background-color: var(--bg3) !important;
}
.markdown-body table tr, .blob-num-context, #user-content-toc td {
    background: var(--bg1) !important;
}
