/* 
 * @date 2020-05-29T05:12:48.020
 */
/* 
 * 变量配置
 * var.css
 */
@-moz-document domain("keylol.com") {
	/*
	 * 变量配置
	 */
	:root {
	    --dark-accent: var(--color600);
	
	    --bg-dark2: #151515;
	    --bg-dark1: #222;
	    --bg-dark3: #444;
	    --bg-dark4: #777;
	
	    --t-dark: #d6d6d6;
	
	    --color500: var(--bg-dark2);
	}
}

/* 
 * @date 2020-05-29T05:12:48.033
 */
/* 
 * 导航栏
 * nav.css
 */
@-moz-document domain("keylol.com") {
	/*
	 * 导航栏
	 * nav.css
	 */
	/* region 导航栏 */
	#nav-menu ul li ul li a {
	    color: var(--t-dark);
	}
	
	.dropdown-menu > li > a {
	    color: var(--t-dark);
	}
	
	#nav-user-action-bar .divider {
	    background: var(--bg-dark1) !important;
	}
	
	.dropdown-menu > li > a:hover {
	    color: var(--t-dark);
	}
	
	/* endregion 导航栏 */
	
	/* 全局 */
	body {
	    background: var(--bg-dark1);
	}
	
	a {
	    color: var(--t-dark);
	}
}

/* 
 * @date 2020-05-29T05:12:48.033
 */
/* 
 * 首页
 * home.css
 */
@-moz-document regexp("https?://keylol.com/(forum.php)?") {
	/*
	 * 首页
	 * home.css
	 */
	/* region 首页 */
	
	/* region banner */
	.xl1 ol > li:nth-child(odd) {
	    background: var(--bg-dark1) !important;
	}
	
	.xl1 ol > li:nth-child(even) {
	    background: var(--bg-dark1) !important;
	}
	
	/* endregion banner */
	
	#wp > div:nth-child(odd):not(:nth-child(1)) {
	    background: var(--bg-dark1) !important;
	}
	
	#wp > div:nth-child(even) {
	    background: var(--bg-dark2) !important;
	}
	
	.subject_row_detail_text_down {
	    color: var(--t-dark) !important;
	}
	
	/* endregion 首页 */
}

/* 
 * @date 2020-05-29T05:12:48.034
 */
/* 
 * 帖子内容
 * post.css
 */
@-moz-document url-prefix("https://keylol.com/t"), url-prefix("https://keylol.com/forum.php?mod=viewthread&t") {
	/*
	 * 帖子内容
	 * post.css
	 */
	/* region 帖子 */
	
	.subforum_left_title_left_down a:nth-child(1) {
	    color: var(--dark-accent);
	}
	
	.subforum_left_title_left_down a:before {
	    background: var(--dark-accent);
	}
	
	/* region 字体 */
	#postlist,
	.ptm.pnpost + div,
	.ptm.pnpost + div + label {
	    color: var(--t-dark);
	}
	
	/* region 分页 */
	.pgs.mtm.mbm.cl {
	
	}
	
	/* endregion 分页 */
	
	/* endregion 字体 */
	
	#postlist,
	#postlist .pls {
	    background: var(--bg-dark2);
	}
	
	.steam_connect_user_bar,
	.pi,
	.po {
	    border: unset;
	}
	
	/* region 帖子*/
	
	/*region code*/
	#postlist .blockcode {
	    border-radius: 5px;
	    border: 1px solid var(--bg-dark1);
	    background: var(--bg-dark1);
	    color: var(--orange500);
	}
	
	#postlist .blockcode li:hover {
	    background: var(--bg-dark1);
	    color: var(--t-dark);
	}
	
	/*endregion code*/
	
	/* region 回复间隔 */
	.ad .pls,
	.ad .plc {
	    background: var(--bg-dark1) !important;
	}
	
	/* endregion 回复间隔 */
	a[id^=postnum] {
	    border-color: var(--bg-dark1) !important;
	}
	
	/* region 引用 */
	.pl blockquote {
	    background: var(--bg-dark1) !important;
	}
	
	.pl .quote:before {
	    background: var(--bg-dark3);
	}
	
	/* endregion 引用 */
	
	/* region 评分 */
	.ratl th, .ratl td, .ratc {
	    border: unset;
	}
	
	/* endregion 评分 */
	
	/* endregion 帖子*/
	
	/* region 分页小条条 */
	.pg strong {
	    /*color: var(--white1);*/
	    background-color: var(--bg-dark1);
	}
	
	#f_pst form {
	    border-top: 5px solid var(--bg-dark1);
	}
	
	.pg a {
	    color: var(--bg-dark1);
	}
	
	.pg a:hover {
	    color: var(--t-dark);
	}
	
	.pg a.nxt:after {
	    border-color: transparent var(--bg-dark1) var(--bg-dark1) transparent;
	}
	
	.pg a.nxt:hover:after {
	    border-color: transparent var(--t-dark) var(--t-dark) transparent;
	}
	
	/* endregion 分页小条条 */
	
	/* region 回复 */
	.tedt .bar,
	.tedt .area,
	.tedt .pt {
	    background: var(--bg-dark1);
	}
	
	.tedt .bar {
	    border: 1px solid var(--bg-dark2);
	}
	
	/* endregion 回复 */
	/* endregion 帖子 */
}

