/* 
 * 高级编辑器
 * new-thread.css
 */
@-moz-document regexp(".+keylol.com.+mod=post&action=(newthread|reply|edit).*") {
	/*
	 * 发新贴
	 * new-thread.css
	 * @url https://keylol.com/forum.php?mod=post&action=newthread&fid=127&cedit=yes&extra=
	 */
	#postform {
	    --icon-width: 26px;
	    --icon-height: 26px;
	    --icon-margin: 2px;
	    --icon-bg: var(--bg-dark1);
	    --icon-bg-hover: var(--color700);
	    --btn: var(--bg-dark1);
	    --btn-bg: var(--color600);
	    background: #0d47a1;
	}
	
	#post_extra {
	    --tab-bg: var(--bg-dark1);
	    --tab-bg-avtive: var(--bg-dark2);
	    border: 1px solid var(--bg-dark2);
	}
	
	#ct.ct2_a,
	.edt .bar,
	.bm {
	    background: var(--bg-dark1) !important;
	}
	
	.edt .bar {
	    border-bottom: 1px solid var(--bg-dark2);
	}
	
	#e_body {
	    color: unset !important;
	    border: 1px solid var(--bg-dark2);
	}
	
	#e_switchercheck {
	    background: unset !important;
	}
	
	.edt .area {
	    background: var(--bg-dark1) !important;
	}
	
	body {
	    color: unset;
	}
	
	.edt .bbar {
	    background: unset;
	    border-top: 1px solid var(--bg-dark2);
	}
	
	#e_body, #e_body .bar {
	    border-radius: 5px 5px 0 0;
	}
	
	#post_extra_c .exfm {
	    border: 1px solid var(--bg-dark2);
	}
	
	.mtm.mbm.pnpost > div:last-child {
	    line-height: 20px;
