/* 
 * @date 2020-05-29T05:14:32.996
 */
/* 
 * 通知
 * notification.css
 */
@-moz-document regexp(".+keylol.com.+mod=space&do=(pm|notice(&view=.*)?).*") {
	/*
	 * 通知
	 * notification.css
	 */
	
	/*region 影藏部分布局*/
	.floatcontainer.doc_header.wp.cl,
	#wp > #ct > .appl > .tbn > .mt.bbda {
	    display: none;
	}
	
	/*endregion 影藏部分布局*/
	
	/*region 大长条*/
	#wp > .wp,
	#wp.wp {
	    max-width: unset;
	    width: 100%;
	    margin: unset;
	}
	
	#pt.bm.cl > div {
	    opacity: 0;
	}
	
	#pt {
	    background: var(--color-accent);
	    height: 55px;
	    padding: 0;
	    border-bottom: unset;
	    -webkit-box-shadow: 0 3px 3px -2px rgba(0, 0, 0, .2), 0 3px 4px 0 rgba(0, 0, 0, .14), 0 1px 8px 0 rgba(0, 0, 0, .12);
	    box-shadow: 0 3px 3px -2px rgba(0, 0, 0, .2), 0 3px 4px 0 rgba(0, 0, 0, .14), 0 1px 8px 0 rgba(0, 0, 0, .12);
	}
	
	#nav-menu {
	    box-shadow: unset;
	}
	
	/*endregion 大长条*/
	
	/* region 主体*/
	/* 整体布局 */
	/* 内容居中 */
	#ct > .mn > .bm.bw0 {
	    width: 1200px;
	    margin: 0 auto;
	}
	
	#ct {
	    padding-top: 10px;
	}
	
	#ct .mn {
	    width: calc(100% - 67px);
	    padding-top: 50px;
	}
	
	#ct > .mn ul.tb.cl > li {
	    float: unset;
	    display: inline-block;
	}
	
	#ct > .mn ul.tb.cl {
	    z-index: 1;
	    position: absolute;
	    top: 86px;
	    left: calc(50%);
	    transform: translate(-50%, -50%);
	    border: unset;
	    line-height: 40px;
	    width: 100%;
	    display: flex;
	    justify-content: center;
	}
	
	/* 小标题 */
	#ct > .mn ul.tb.cl > li > a {
	    background: unset;
	    border: unset;
	    color: var(--white1);
	    opacity: .8;
	    font-size: 14px !important;
	    padding: 0 35px;
	}
	
	#ct > .mn ul.tb.cl > li.o,
	#ct > .mn ul.tb.cl > li.o > a {
	    background: unset;
	    margin: unset;
	    box-shadow: unset !important;
	    line-height: unset;
	    height: unset;
	    background-image: unset !important;
	}
	
	.tb .a a{
	    border-bottom: 2px solid var(--white1) !important;
	    opacity: 1 !important;
	    font-weight: unset;
	}
	
	/* 微调列表布局 */
	#deletepmform {
	    margin-bottom:120px;
	}
	
	#deletepmform > .xld.xlda.pml.mtm.mbm > dl > dd.m.avt img,
	.xlda .avt img {
	    border-radius: 50%;
	    -webkit-box-shadow: 0 3px 3px -2px rgba(0, 0, 0, .2), 0 3px 4px 0 rgba(0, 0, 0, .14), 0 1px 8px 0 rgba(0, 0, 0, .12);
	    box-shadow: 0 3px 3px -2px rgba(0, 0, 0, .2), 0 3px 4px 0 rgba(0, 0, 0, .14), 0 1px 8px 0 rgba(0, 0, 0, .12);
	}
	#deletepmform > .xld.xlda.pml.mtm.mbm > dl > dd.m.avt img {
	    border-radius: 50%;
	}
	.xlda .avt img {
	    border-radius: 50%;
	    margin-right:20px;
	}
	.xlda dl {
	    padding-top: 5px;
	    padding-bottom: 5px;
	}
	
	/* region 左侧菜单 */
	
	#ct.ct2_a, #ct {
	    background: unset;
	}
	
	.appl {
	    float: unset;
	    overflow: unset;
	    margin-bottom: unset;
	    padding: unset;
	}
	
	.tbn li.a,
	.tbn li:hover {
	    background-color: var(--grey200);
	    transition: .2s;
	}
	
	.tbn li.a a {
	    color: var(--color500) !important;
	}
	
	#ct > .appl > .tbn {
	    margin: unset;
	}
	
	#ct > .appl > .tbn > ul > li {
	    text-align: unset;
	    height: unset;
	    line-height: unset;
	    padding: unset;
	    margin: unset;
	    overflow: unset;
	    width: 200px;
	    padding-left: 16px;
	}
	
	#ct > .appl > .tbn > ul > li > a {
	    display: unset;
	    padding: unset;
	    margin: unset;
	    top: unset;
	    color: var(--black2);
	    font-size: 16px;
	    height: 48px;
	    line-height: 48px;
	    padding-left: 48px;
	}
	
	.notice_pm, .notice_all, .notice_mypost, .notice_interactive, .notice_system, .notice_manage, .notice_app {
	    display: none;
	}
	
	#ct > .appl > .tbn > ul > li > a:before {
	    content: "";
	    display: block;
	    width: 20px;
	    height: 20px;
	    background: var(--black2);
	    -webkit-mask-repeat: no-repeat;
	    position: absolute;
	    top: 1px;
	    /*     border-radius: 50%; */
	}
	#ct > .appl > .tbn > ul > li.a > a:before {
	    background: var(--color500);
	}
	/* 消息 */
	#ct > .appl > .tbn > ul > li:nth-child(1) > a:before {
	    -webkit-mask-image: url(data:image/png;base64,);
	}
	
	/* 全部提醒 */
	#ct > .appl > .tbn > ul > li:nth-child(2) > a:before {
	    -webkit-mask-image: url(data:image/png;base64,);
	}
	
	/* 我的帖子 */
	#ct > .appl > .tbn > ul > li:nth-child(3) > a:before {
	    -webkit-mask-image: url(data:image/png;base64,);
	}
	
	/* 坛友互动 */
	#ct > .appl > .tbn > ul > li:nth-child(4) > a:before {
	    -webkit-mask-image: url(data:image/png;base64,);
	}
	
	/* 系统提醒 */
	#ct > .appl > .tbn > ul > li:nth-child(5) > a:before {
	    -webkit-mask-image: url(data:image/png;base64,);
	}
	
	/* 应用提醒 */
	#ct > .appl > .tbn > ul > li:nth-child(6) > a:before {
	    -webkit-mask-image: url(data:image/png;base64,);
	}
	
	/* endregion 左侧菜单 */
	
	/* region 列表 */
	/* 圆形头像*/
	#deletepmform > .xld.xlda.pml.mtm.mbm > dl > dd.m.avt {
	    border-radius: 50%;
	}
	
	/* endregion 列表 */
	
	/* region 页码*/
	.pgb.y {
	    display: none;
	}
	
	.pgs.mtm.mbm.cl {
	    margin: -10px 0 0 0 !important;
	    padding: unset !important;
	}
	
	.pg a, .pg strong, .pgb a, .pg label {
	    background-color: transparent;
	}
	
	.pg strong {
	    color: var(--white1);
	    background-color: var(--color500);
	}
	
	.pg a {
	    color: var(--color500);
	    transition: all .2s ease-in-out;
	}
	
	.pg a:hover {
	    background-color: rgba(0, 0, 0, .1);
	}
	
	.pg a:active {
	    color: var(--white1);
	    background-color: var(--color500);
	}
	
	.pg label {
	    display: none;
	}
	
	/* region 上一页按钮*/
	
	.pg a.prev {
	    width: 32px;
	    height: 32px;
	    padding: 0;
	    background-image: unset !important;
	    background-color: transparent;
	}
	
	.pg a.prev:hover {
	    background: rgba(0, 0, 0, .1);
	}
	
	.pg a.prev:active {
	    background: var(--color500);
	}
	
	.pg a.prev:after {
	    display: inline-block;
	    content: '';
	    position: relative;
	    top: 3px;
	    left: 7px;
	    border: 4px solid;
	    border-color: transparent var(--color500) var(--color500) transparent;
	    transform: rotate(135deg);
	}
	
	.pg a.prev:active:after {
	    border-color: transparent var(--white1) var(--white1) transparent;
	}
	
	/* endregion*/
	
	/* region 下一页按钮*/
	.pg a.nxt {
	    width: 0;
	    height: 32px;
	    padding: 0 0 0 32px;
	    position: relative;
	    font-size: 0;
	    overflow: hidden;
	    white-space: nowrap;
	    background: unset;
	}
	
	.pg a.nxt:hover {
	    background: rgba(0, 0, 0, .1);
	}
	
	.pg a.nxt:active {
	    background: var(--color500);
	}
	
	.pg a.nxt:after {
	    display: inline-block;
	    content: '';
	    position: relative;
	    top: 7px;
	    left: -22px;
	    border: 4px solid;
	    border-color: transparent var(--color500) var(--color500) transparent;
	    transform: rotate(-45deg);
	}
	
	.pg a.nxt:active:after {
	    border-color: transparent var(--white1) var(--white1) transparent;
	}
	
	/* endregion*/
	
	/* region 阴影*/
	
	.pgs.mtm.mbm.cl {
	    /*-webkit-box-shadow: 0 6px 6px -3px rgba(0, 0, 0, .2), 0 10px 14px 1px rgba(0, 0, 0, .14), 0 4px 18px 3px rgba(0, 0, 0, .12);*/
	    /*box-shadow: 0 6px 6px -3px rgba(0, 0, 0, .2), 0 10px 14px 1px rgba(0, 0, 0, .14), 0 4px 18px 3px rgba(0, 0, 0, .12)*/
	}
	
	/* endregion*/
	
	/* endregion*/
	
	/*endregion 主体*/
}

/* 
 * @date 2020-05-29T05:14:32.997
 */
/* 
 * 页脚
 * foot.css
 */
@-moz-document domain("keylol.com") {
	/*
	 * 变量配置
	 */
	
	/* 文字效果 */
	.subforunm_foot_intro a,
	.subforunm_foot_intro div {
	    color: var(--white1)
	}
	
	.subforunm_foot_banner a,
	.subforunm_foot_intro a {
	    position: relative;
	    display: inline-block;
	    overflow: hidden;
	    text-decoration: none;
	    vertical-align: top;
	    outline: 0;
	}
	
	.subforunm_foot_banner a:before,
	.subforunm_foot_intro a:before {
	    position: absolute;
	    top: auto;
	    bottom: 0;
	    left: 0;
	    width: 100%;
	    height: 2px;
	    content: ' ';
	    background-color: white;
	    -webkit-transition: all .2s;
	    transition: all .2s;
	    -webkit-transform: scaleX(0);
	    transform: scaleX(0);
	    -webkit-backface-visibility: hidden;
	    backface-visibility: hidden;
	}
	
	.subforunm_foot_banner a:focus:before,
	.subforunm_foot_banner a:hover:before,
	.subforunm_foot_intro a:focus:before,
	.subforunm_foot_intro a:hover:before {
	    -webkit-transform: scaleX(1);
	    transform: scaleX(1);
	}
	
	/* 布局 */
	
	.subforunm_foot {
	    margin-top: 60px;
	    padding-bottom: 10px;
	}
	
	.subforunm_foot,
	.subforunm_foot_banner {
	    background: var(--color500);
	}
	
	.subforunm_foot_bg:before {
	    content: '';
	    display: block;
	    position: relative;
	    top: 117px;
	    left: 20px;
	    width: 620px;
	    height: 100px;
	    background: #fff;
	    -webkit-mask-image: url(https://keylol.com/template/steamcn_metro/src/img/common/icon_with_text_256h.png);
	    -webkit-mask-size: 50%;
	    -webkit-mask-repeat: no-repeat;
	}
	
	.subforunm_foot_bg img {
	    opacity: 0;
	}
}

/* 
