@-moz-document domain(www.omegle.com) {

body
  {
background-color: #1d1d1d !important;
    color: #C4CDD4;
  }

 .logwrapper
  {background-color: #423C53;
    border: none !important;
    background-repeat: no-repeat;                 
    background-size: cover;           
    top: .5em;
  }

 .strangermsg
  {
    color: #C4CDD4;
  }

  .youmsg
  {
    color: #C4CDD4;
  }

  .disconnectbtn,
.sendbtn
  {
    background: #000000 !important;
    color: #C4CDD4 !important;
  }

  .chatmsgwrapper,
textarea
  {
    background: #3B163B !important;
    color: #ffffff !important;
  }

  a:link
  {
    color: #C4CDD4 !important;
  }

  #header
  {
    display: none !important; }

  div#footer
  {
    display: none;
  }

  div#introtext a img
  {
    display: none;
  }

  #onlinecount
  {
    color: #cccccc !important;
    top: 5px !important;
  }

  div.logwrapper[style],
div.controlwrapper[style]
  {
    margin-right: .5em !important;
  }

  .statuslog
  {
    color: #C4CDD4!important;
  }

  .questionHeading
  {
    background: #4D4D4D;
    color: #D4D4D4;
  }

  .questionText
  {
    background: #222;
  }

  .topictag
  {
    background: #000;
  }

  .topictageditor
  {
    background: #3d3d3d!important;
    border: 1px solid #505050!important;
  }

  img[src*="exclamationog.png"],
img[src*="exclamationog@2x.png"]
  {
    height: 0 !important;
    width: 0 !important;
    padding-left: 25px !important;
    padding-top: 25px !important;
    background: url(http://i.imgur.com/3pnbg5e.png) no-repeat !important;
    background-size: 100% !important;
  }

  img[src*="textbtn.png"], 
img[src*="textbtn@2x.png"]
  {
    height: 0 !important;
    width: 0 !important;
    padding-left: 124px !important;
    padding-top: 50px !important;
    background: url(http://i.imgur.com/7FV5emK.png) no-repeat !important;
    background-size: 100% !important;
  }

  img[src*="videobtn-enabled.png"],
img[src*="videobtn-enabled@2x.png"]
  {
    height: 0 !important;
    width: 0 !important;
    padding-left: 124px !important;
    padding-top: 50px !important;
    background: url(http://i.imgur.com/IObxrIp.png) no-repeat !important;
    background-size: 100% !important;
  }

  img[src*="videobtn-disabled.png"],
img[src*="videobtn-disabled@2x.png"]
  {
    height: 0 !important;
    width: 0 !important;
    padding-left: 124px !important;
    padding-top: 50px !important;
    background: url(http://i.imgur.com/2KY9hPB.png) no-repeat !important;
    background-size: 100% !important;
  }

  img[src*="newchatbtn.png"],
img[src*="newchatbtn@2x.png"]
  {
    height: 0 !important;
    width: 0 !important;
    padding-left: 170px !important;
    padding-top: 50px !important;
    background: url(http://i.imgur.com/jFTIxxw.png) no-repeat !important;
    background-size: 100% !important;
  }

  #tagline img
  {
    display: none !important;
  }

  #intro
  {
    border: 1px solid #000;
    background: rgb(20, 20, 20);
    left: 0px;
    -webkit-box-shadow: inset 0 0 .0em #000;
    -moz-box-shadow: inset 0 0 .0em #000;
    box-shadow: inset 0 0 .0em #000;
  }

  #startachat,
#intoheader
  {
    color: rgb(200, 200, 200);
  }

  #mobilesitenote
  {
    display: none;
  }

  #monitoringnotice
  {
    border: 1px solid #505050;
    box-shadow: 0 0 0em #000000;
    -webkit-box-shadow: 0 0 0em #000000;
    background: #3D3D3D!important;
  }

  #monitoringnotice h2
  {
    background: #3D3D3D;
  }

  .youmsg .msgsource,
.strangermsg .msgsource
  {
    display: none;
  }

  .youmsg:before
  {
    content: "Y:";
    font-weight: bold;
  }

  .strangermsg:before
  {
    content: "S:";
    font-weight: bold;
  }

  .disconnectbtnwrapper,
.sendbtnwrapper
  {
    border: none !important;
    background: #000000!important;
  }

  .chatmsgwrapper
  {
    border: none !important;
  }

  #onlinecount
  {
    font-size: 1em !important;
    color: #ccc !important;
  }

  #onlinecount strong
  {
    font-size: 1em !important;
    color: #ccc !important;
  }

  #sharebuttons
  {
    display: none !important;
  }

  .topictagtext,
.topictagdelete
  {
    color: #fff !important;
  }

  [target="_blank"] span
  {
    color: #777 !important;
    background: #333 !important;
  }

  a#spymodebtn[style],
a#videobtnunmoderated[style],
a[style="display: block; margin-top: 0.5em; border: 1px solid rgb(204, 204, 204); color: black; text-align: center; text-decoration: none; padding: 0.25em; border-radius: 0.5em; position: relative; background: rgb(238, 238, 238);"]
  {
    background: #3d3d3d !important;
    border-color: #505050 !important;
  }

  #topterms
  {
    display: none;
  }

  .newtopicinput
  {
    color: #ffffff;
  }

  .conversationgreat
  {
    background: #00459C;
  }

  #onlinecount:before
  {
    font-size: 10px;
    color: #555;
  }
}

@-moz-document domain("www.nitrotype.com")
{
 body
  {
    background-color: #2d2e2e;
    overflow-y: scroll;
    background: none;
    overflow-x: hidden;
    position: relative;
  }

  .news-teaser
  {
    color: #fff;
  }

  .flash--beta
  {
    display: none;
  }

  .theme--pDefault .profile-bg
  {
    background: url("https://cdn.discordapp.com/attachments/665021465665470464/715325417073934384/thanc.png") top left repeat, linear-gradient(0deg, #2d2e2e 60%, #ffffff 100%);
    color: #d3d3d3;
    background-size: 100%
  }

  .theme--pDefault .profile-bgHead
  {
    background: url("https://cdn.discordapp.com/attachments/665021465665470464/715325417073934384/thanc.png") top left repeat, linear-gradient(0deg, #2d2e2e 10%, #ffffff 100%);
   background-size: 100%
  }

  .theme--pDefault.is-gold .profile-bg
  {
    background: url("https://cdn.discordapp.com/attachments/665021465665470464/715325417073934384/thanc.png") top left repeat, linear-gradient(0deg, #2d2e2e 10%, #ffffff 100%);
   background-size: 100%
  }

  .card--a
  {
    background-color: #fff0;
    color: #ffffff;
  }

  .card--shadow-l
  {
    box-shadow: none;
  }
  .theme--pDefault .profile-levelNum {
    color: #ffffff;
    text-shadow: 0 2px 2px rgba(2, 2, 2, 0.25);
  }
  .theme--pDefault .profile-title {
    color: #ffffff;
    text-shadow: 0 2px 2px rgba(2, 2, 2, 0.25);
  }
  .btn--inky, .btn--inky:focus:not(:hover) {
    color: #ffffff;
  }
  .card--b {
    color: #fff;
  }
  .theme--pDefault .profile-body {
    color: #ffffff;
  }

  html
  {
    background: fixed url("https://cdn.discordapp.com/attachments/665021465665470464/715325975570939904/Screen_Shot_2020-05-27_at_6.09.48_PM.png") repeat, #2d2e2e;
    box-sizing: border-box;
    color: #2d2e2e;
    font-family: "Montserrat", sans-serif;
    font-size: 16px;
    font-weight: 300;
    line-height: 1.6;
  }

  .structure-ad
  {
    display: none;
  }

  .ad
  {
    display: none;
    position: relative;
    margin-right: auto;
    margin-left: auto;
    text-align: center;
  }

  .ad--sq
  {
    display: none;
    width: 336px;
    height: 280px;
    background: #f2f2f2;
  }

  .ad--ldrbrd
  {
    display: none;
    width: 728px;
    height: 90px;
    background: #f2f2f2;
  }

  .ad--ldrbrd_l
  {
    display: none;
    width: 970px;
    height: 90px;
  }

  .ad--fixed
  {
    display: none;
    position: fixed;
    top: 30px;
  }

  .structure-header
  {
    margin-left: auto;
    margin-right: auto;
    max-width: 1020px;
    padding-left: 0px;
    padding-right: 0px;
  }

  .card--grit
  {
    background-image: url("https://cdn.discordapp.com/attachments/665021465665470464/715328318374805574/huh.png");
      background-size: 100%
  }

  .table--b .table-cell
  {
    border-bottom: none;
  }

  .tc-ts
  {
    color: #f7f4f4;
  }

  .card--b
  {
    color: #fff;
  }

  .tc-emerald
  {
    color: #00ff6c;
  }

  .tc-i
  {
    color: #fff;
  }

  .tc-dodger
  {
    color: #0004ff;
  }

  .tc-lemon
  {
    color: #ffcb00;
  }

  .news--solo .news-body p
  {
    color: #fff;
  }

  .news--solo .news-body .yellow
  {
    color: #a28100;
  }

  .link--nav
  {
    color: #000000ba;
  }

  .link--nav.is-current
  {
    color: #000;
  }

  .link--nav:hover
  {
    color: #000;
  }

  .bg--gradient
  {
    background: #00000000;
  }

  path:nth-child(2)
  {
    fill: #fff;
  }

  path:nth-child(3)
  {
    fill: #fff;
  }

  path:nth-child(4)
  {
    fill: #fff;
  }

  path:nth-child(5)
  {
    fill: #fff;
  }

  path:nth-child(6)
  {
    fill: #fff;
  }

  path:nth-child(8)
  {
    fill: #fff;
  }
   .dash-copyContainer
  {
    background: #1d1e23;
    -webkit-box-shadow: 0px 1px 157px 10px rgba(0,0,0,0.78);
    box-shadow: 0px 1px 157px 10px rgba(0,0,0,0.78);
    border-radius: 5px;
    flex: 1;
    overflow: hidden;
    padding: 15px;
    display: flex;
  }

  .dash-copy
  {
    color: #fff;
    font-family: "Roboto Mono", "Courier New", Courier, "Lucida Sans Typewriter", "Lucida Typewriter", monospace;
    font-size: 28px;
  }

  ::selection
  {
    background-color: #1976c6;
    color: white;
  }
}

